Name = "";
Country = "";

customer = (from customer_Row in Db.Customer
		    where (customer_Row.Company == Company && customer_Row.CustID == CustID.ToString())
		    select customer_Row).FirstOrDefault();

if (customer == null) { return false; }
else {
  Name = customer.Name;  
  Country = customer.Country;
  return true;
}

