            /* Get the slat insert part and qty from the CSV file */
            slatinsertpart = PCLookUp.DataLookup("TGPPAPanelPartDetails", "slat_infill_part_num", panelpartprefix2);
            slatinsertqty = PCLookUp.DataLookup("TGPPAPanelPartDetails", "num_panel_infills", panelpartprefix2);

            /* Get parameter determining if adaptors are required from the CSV file */
            Inputs.var_adaptorrqd.Value = PCLookUp.DataLookup("TGPPAPanelPartDetails", "include_adaptors", panelpartprefix2);


            adaptorpart1 = PCLookUp.DataLookup("TGPPAPanelPartDetails", "adaptor_part1", panelpartprefix2);
            adaptor1qty = UDMethods.ConvertToDecimal(PCLookUp.DataLookup("TGPPAPanelPartDetails", "adaptor_part1_qty", panelpartprefix2));

            adaptorpart2 = PCLookUp.DataLookup("TGPPAPanelPartDetails", "adaptor_part2", panelpartprefix2);
            adaptor2qty = UDMethods.ConvertToDecimal(PCLookUp.DataLookup("TGPPAPanelPartDetails", "adaptor_part2_qty", panelpartprefix2));

            adaptorpart3 = PCLookUp.DataLookup("TGPPAPanelPartDetails", "adaptor_part3", panelpartprefix2);
            adaptor3qty = UDMethods.ConvertToDecimal(PCLookUp.DataLookup("TGPPAPanelPartDetails", "adaptor_part3_qty", panelpartprefix2));

            if (panelpart2prefix != "")
            {
                Inputs.var_part2.Value = panelpart2prefix + "-" + Inputs.var_part1.Value.Entry(1, "-");
                if (Inputs.var_part2.Value != "") { Inputs.var_partqty2.Value = 1; }
            }

            if ((slatinsertpart != "") && (slatinsertqty != ""))
            {
                Inputs.var_part3.Value = slatinsertpart;
                if (Inputs.var_part3.Value != "") { Inputs.var_partqty3.Value = UDMethods.ConvertToDecimal(slatinsertqty); }
            }

