        }

        if ((Inputs.chk_adaptors.Value == true) && (adaptorpart2 != ""))
        {
            Inputs.var_part5.Value = adaptorpart2;
            Inputs.var_partqty5.Value = adaptor2qty;
        }

        if ((Inputs.chk_adaptors.Value == true) && (adaptorpart3 != ""))
