  decimal qtyonhand = 0m;
  decimal demandqty = 0m;
  decimal availqty = 0m;
  bool fromstock = false;

/* Determine the FG mode to operate in */

/* Set the BOM value visibility */
Inputs.var_jobmtlfgassy.Invisible = false;
