Inputs.var_bomstatus.Invisible = false;

Inputs.var_jobmtlfgassy.Value = "";
Inputs.var_fgparttype.Value = "";

string assystockmodelookup = "";
assystockmodelookup = PCLookUp.DataLookup("TBPAssembly_Stocking_Mode", "mode", "fgassy");

/* Calculate the bool available_qty for the finished assembly */
bool partrecordexists = false;
string partrecordtype = "";
string PartTypeCode = "";

bool available_part = UDMethods.GetPartTypeCode(Context.CompanyID,Inputs.var_sku.Value, out PartTypeCode);

if (available_part) {
  partrecordexists = true;
  partrecordtype = PartTypeCode;
}

if (partrecordexists == true) {
   
 Inputs.var_fgparttype.Value = partrecordtype;

 decimal OnHandQty = 0m;
 decimal DemandQty = 0m;
  
  /* Search for a FG Part record and calculate the bool available_qty's */
  bool available_PartQty = UDMethods.GetPartQty(Context.CompanyID,Inputs.var_smart2.Value, out OnHandQty, out DemandQty);

  if (available_PartQty) {
		qtyonhand = OnHandQty;
    demandqty = DemandQty;
    availqty = qtyonhand - demandqty;
  }
  
  /* Make Direct Mode Only */
  if (assystockmodelookup == "NONE") {
    Inputs.chk_rework.Value = true;
    Inputs.var_configtype.Value = "MFG";
  }
  
  
  /* only take fg assy from stock if the bool available_qty fulfills the full demand */
  if (assystockmodelookup == "FULL") {
  if (availqty < Inputs.dec_soqty.Value) {fromstock = false;}
  if (availqty == Inputs.dec_soqty.Value) {fromstock = true;}
  if (availqty > Inputs.dec_soqty.Value) {fromstock = true;}
    
    
    /* Manufactured Part Decision */
    if (partrecordtype == "M") {
    if (fromstock == true) {Inputs.chk_rework.Value = false;}
    if (fromstock == false) {Inputs.chk_rework.Value = true;}
      /* Determine Config Type */
    if (fromstock == true) {Inputs.var_configtype.Value = "M-STK";}
    if (fromstock == false) {Inputs.var_configtype.Value = "MFG";}
    }
    
    if (partrecordtype == "P") {
    if (fromstock == true) {Inputs.chk_rework.Value = false;}
    if (fromstock == false) {Inputs.chk_rework.Value = false; /* Rqd due to a bug in CTP which ignores the sales release if a Purchased part record exists */}
      /* Determine Config Type */
      Inputs.var_configtype.Value = "PUR";
    }
    
    
    /* will take fg assy from stock even if the bool available_qty does not fulfill the full demand */
    if (assystockmodelookup == "ANY") {
    if (availqty >1) {fromstock = true;}
    if (availqty ==0) {fromstock = false;}
      
      /* Manufactured Part Decision */
      if (partrecordtype == "M") {
      if (fromstock == true) {Inputs.chk_rework.Value = false;}
      if (fromstock == false) {Inputs.chk_rework.Value = true;}
        /* Determine Config Type */
      if (fromstock == true) {Inputs.var_configtype.Value = "M-STK";}
      if (fromstock == false) {Inputs.var_configtype.Value = "MFG";}
      }
      
      if (partrecordtype == "P") {
      if (fromstock == true) {Inputs.chk_rework.Value = false;}
      if (fromstock == false) {Inputs.chk_rework.Value = false; /* Rqd due to a bug in CTP which ignores the sales release if a Purchased part record exists */}
        /* Determine Config Type */
        Inputs.var_configtype.Value = "PUR";
      }
    }
  }
}


if (partrecordexists == false) {
  Inputs.chk_rework.Value = true;
  /* Determine Config Type */
  Inputs.var_configtype.Value = "MFG";
}

/* String to indicate on Last Page that the fg assy will come from a stocked part */

if (Inputs.chk_rework.Value == false) {
  string assystring = "";
  assystring = "Assy: " + Inputs.dec_soqty.Value.ToString() + " x " + Inputs.var_smart2.Value + " will be taken from Stock.";
	Inputs.var_fgvalidity.Value = assystring;
	Inputs.var_fgvalidity.ToolTip = availqty.ToString();
	Inputs.var_bomvalidity .Invisible = true;
}

if (Inputs.chk_rework.Value == true) {
  Inputs.var_fgvalidity.Value = "Make Direct";
	Inputs.var_fgvalidity.ToolTip = "0";
	Inputs.var_bomvalidity .Invisible = false;
}


if (((Inputs.var_reldefault.Value == "pick") && (partrecordexists != true)) || ( (Inputs.chk_jobmtloverride.Value==true) )) {
  
  string assystringpickdefault = "";
  assystringpickdefault = "Assy: " + Inputs.dec_soqty.Value.ToString() + " x " + Inputs.var_smart2.Value + " to be supplied via Assy Job Mtl.";
  Inputs.var_bomvalidity.Value = assystringpickdefault;
  Inputs.var_fgvalidity.Value = "Make";
Inputs.var_jobmtlfgassy.Invisible = false;
Inputs.var_postpartnum.Invisible = true;
Inputs.var_postassybom.Invisible = true;
