if ((Inputs.var_webpartclass.Value=="SWEB") && ( Inputs.var_webpartclassdual.Value=="SWEB")) {Inputs.cmb_productgroup.Value = "Tensabarrier Post Range";}
if ((Inputs.var_webpartclass.Value=="CUTW") && ( Inputs.var_webpartclassdual.Value=="CUTW")) {Inputs.cmb_productgroup.Value = "Tensabarrier Post Range";}
if ((Inputs.var_webpartclass.Value=="CUTW") && ( Inputs.var_webpartclassdual.Value=="SWEB")) {Inputs.cmb_productgroup.Value = "Tensabarrier Post Range";}
if ((Inputs.var_webpartclass.Value=="SWEB") && ( Inputs.var_webpartclassdual.Value=="CUTW")) {Inputs.cmb_productgroup.Value = "Tensabarrier Post Range";}
  
  
if ((Inputs.var_webpartclass.Value=="CWEB") && ( Inputs.var_webpartclassdual.Value=="SWEB")) {Inputs.cmb_productgroup.Value = "Tensamedia";}
if ((Inputs.var_webpartclass.Value=="SWEB") && ( Inputs.var_webpartclassdual.Value=="CWEB")) {Inputs.cmb_productgroup.Value = "Tensamedia";}
if ((Inputs.var_webpartclass.Value=="CUTW") && ( Inputs.var_webpartclassdual.Value=="CWEB")) {Inputs.cmb_productgroup.Value = "Tensamedia";}
}


