}
else
{
	partprefix = PCLookUp.DataLookup("TBPPostSelection", "dual_post_part_no", Inputs.cmb_postselection.Value);
	partsuffix = PCLookUp.DataLookup("TBPPostFinish", "colorcode", Inputs.cmb_postfinishselection.Value);
	baserange = PCLookUp.DataLookup("TBPPostSelection", "dual_base_range", Inputs.cmb_postselection.Value);
}

if (partprefix != "") {
  Inputs.var_postpartnum.Value = partprefix + "-" + partsuffix;
  Inputs.var_postbaserangelookup.Value = "r" + baserange;
  Inputs.var_postcassetterangelookup.Value = "r" + cassetterange;
  Inputs.var_dualcassetterangelookup.Value = "r" + dualcassetterange;
}

if (partprefix == "") {
  Inputs.var_postpartnum.Value = "";
  Inputs.var_postqty.Value=0;
  Inputs.var_postbaserangelookup.Value = "r" + baserange;
  Inputs.var_postcassetterangelookup.Value = "r" + cassetterange;
  Inputs.var_dualcassetterangelookup.Value = "r" + dualcassetterange;
}



