}
if (postassyprefix =="") {Inputs.var_postassybom.Value = "";}
if (Inputs.var_postassybom.Value !="") {Inputs.var_postassyqty.Value = 1;}
if (Inputs.var_postassybom.Value == "") {Inputs.var_postassyqty.Value = 0;}


