  Inputs.var_allenkeypartnum.Value = Inputs.var_slimlineposthead.Value + "-" + "33S";
  Inputs.var_allenkeyqty.Value=1;
}


if ((Inputs.var_slimlineposthead.Value!="") && ( Inputs.var_smartcodepost.Value!="888")) {
  Inputs.var_allenkeypartnum.Value = Inputs.var_slimlineposthead.Value + "-" + partsuffix;
  Inputs.var_allenkeyqty.Value=1;
}


