/* Looks up whether the post selected can be Dualline returns True from csv */
duallineoption = PCLookUp.DataLookup("TBPPostSelection", "dual_option", Inputs.cmb_postselection.Value);

Inputs.var_duallineoption.Value = duallineoption;
if (Inputs.var_duallineoption.Value == "") {Inputs.chk_duallineoption.Value = false;}

/* Returns from csv post finish column search header */
if (Inputs.chk_duallineoption.Value == false) {
Inputs.var_postfinishcolumnlookup.Value = PCLookUp.DataLookup("TBPPostSelection", "post_finish_column_search", Inputs.cmb_postselection.Value);}

/* Returns from csv sub post finish column search header */
Inputs.var_subpostfinishcolumnlookup.Value = PCLookUp.DataLookup("TBPPostSelection", "subpost_finish_column_search", Inputs.cmb_postselection.Value);

/* Looks up post packing part num from csv */
if (Inputs.cmb_postselection.Value != "Stowaway SA1") {
  Inputs.var_postpackingpartnum.Value = PCLookUp.DataLookup("TBPPostSelection", "post_packing_box", Inputs.cmb_postselection.Value);
if (Inputs.var_postpackingpartnum.Value!="") {Inputs.var_postpackingqty.Value = 1;}
if (Inputs.var_postpackingpartnum.Value=="") {Inputs.var_postpackingqty.Value = 0;}
}

/* Looks up if post can be packed as pairs from csv */
Inputs.var_packaspairs.Value = PCLookUp.DataLookup("TBPPostSelection", "pack_as_pairs", Inputs.cmb_postselection.Value);
if (Inputs.var_packaspairs.Value == "") {Inputs.chk_utilitypair.Value = false;}
/* if Inputs.var_packaspairs.Value = "True" then Inputs.chk_utilitypair.Value = true. */

/* Looks up if post by default is a twin pack and sets pack as pair parameter from csv */
Inputs.var_defaultpackaspairs.Value = PCLookUp.DataLookup("TBPPostSelection", "purchasedaspairs_stockedaseach", Inputs.cmb_postselection.Value);
if (Inputs.var_defaultpackaspairs.Value == "True") {Inputs.chk_utilitypair.Value = true;}

/* Looks up if post can have reflective strips from csv */
Inputs.var_reflectiveon.Value = PCLookUp.DataLookup("TBPPostSelection", "reflective_strip", Inputs.cmb_postselection.Value);
if (Inputs.var_reflectiveon.Value=="") {Inputs.rbn_reflectivestrip.Value = "3";}

/* Looks up if post can have an Allen Key from csv */
Inputs.var_allenkeyoption.Value = PCLookUp.DataLookup("TBPPostSelection", "allenkey_option", Inputs.cmb_postselection.Value);
if ( Inputs.var_allenkeyoption.Value != "TRUE") {Inputs.chk_allenkey.Value = false;}

/* Sets Defaults based on post selection */
