/* Returns from csv if slug is an option */
Inputs.var_slugoption.Value = PCLookUp.DataLookup("TBPPostSelection", "slug_option", Inputs.cmb_postselection.Value);

/* Returns from csv if utility ring is an option */
Inputs.var_postutilityringlookup.Value = PCLookUp.DataLookup("TBPPostSelection", "utility_ring", Inputs.cmb_postselection.Value);

/* Returns from csv smartcode for post */
if (Inputs.chk_utilitypair.Value == false) {
  Inputs.var_smartcodepost.Value = PCLookUp.DataLookup("TBPPostSelection", "smartcode_entry", Inputs.cmb_postselection.Value);
}

if (Inputs.chk_utilitypair.Value == true) {
