  Inputs.var_smartcodepost.Value = smartcodeinterim + "/2";
}


if (Inputs.cmb_postselection.Value == "Qmatic HeavyDuty par pair") {Inputs.chk_utilitypair.Value = true;}
if (Inputs.cmb_postselection.Value == "Qmatic HeavyDuty par pair") {Inputs.var_qtymultiplier.Value = 2;}


