  decimal webaddonbom = 0m;

  webaddonbom = UDMethods.ConvertToDecimal(PCLookUp.DataLookup("TBPWallMCassetteRange", "web_internal_allowance", webuom));
  
  if (Inputs.dec_customweblength.Value == 0) {
    Inputs.var_webqty.Value = UDMethods.ConvertToDecimal(Inputs.cmb_webbinglength.Value) + webaddonbom;
  }
  
  if (Inputs.dec_customweblength.Value != 0) {
    Inputs.var_webqty.Value = Inputs.dec_customweblength.Value + webaddonbom;
  }
}

if (Inputs.var_webpartnum.Value == "") {
  Inputs.var_webqty.Value = 0;
}


/* Sets Wedge Details if webbing is selected */
/* Return values from csv file based on cassette type selected */
if (Inputs.cmb_webbingfinish.Value !="") {
  wedgepartnumlookup = PCLookUp.DataLookup("WebbingCutLengthConversionFactor", "wedge_partnum", Inputs.cmb_cassetteselection.Value);
  
if (wedgepartnumlookup != "") {Inputs.var_wedgepartnum.Value = wedgepartnumlookup;}
if (wedgepartnumlookup == "") {Inputs.var_wedgepartnum.Value = "";}
  
if (Inputs.var_wedgepartnum.Value == "") {Inputs.var_wedgeqty.Value =  0;}
if (Inputs.var_wedgepartnum.Value != "") {Inputs.var_wedgeqty.Value = 1;}
}

/* *************************************************************************************************************** */

/* Determines webbing description for use in comment string */
string desc = "";
available_part = UDMethods.GetPartDescription(Context.CompanyID,Inputs.var_webpartnum.Value, out desc);

if (available_part) {
  string webinfo = "";

  webinfo = Inputs.cmb_webbingfinish.Value + "/" + desc;
  
  if ( webinfo .Length < 65) {
    //FieldToolTip('Inputs.cmb_webbingfinish.Value',webinfo);
    Inputs.cmb_webbingfinish.ToolTip = webinfo;
  }
  
  if (webinfo .Length > 65) {
    //FieldToolTip('Inputs.cmb_webbingfinish.Value',Inputs.cmb_webbingfinish.Value);
    Inputs.cmb_webbingfinish.ToolTip = Inputs.cmb_webbingfinish.Value;
  }
  
}

/* *************************************************************************************************************** */


/* Determines webbing Part Class to enable identification of core or solution sale */
string PartClass = "";
available_part = UDMethods.GetPartClass(Context.CompanyID,Inputs.var_webpartnum.Value, out PartClass);
if (available_part) {
  Inputs.var_webpartclass.Value = PartClass;
}


/* Standard Webbing Variant Only */
if ((Inputs.cmb_webvar.Value == "Standard") || ( Inputs.cmb_webvar.Value == "")) {
  if (Inputs.var_webpartsearch.Value != "11513") {
  if ((Inputs.var_webpartclass.Value=="CWEB") && ( Inputs.var_webpartnum.Value != "")) {Inputs.cmb_productgroup.Value = "Tensamedia";}
  if ((Inputs.var_webpartclass.Value=="SWEB") && ( Inputs.var_webpartnum.Value != "")) {Inputs.cmb_productgroup.Value = "Tensabarrier Post Range";}
  if ((Inputs.var_webpartclass.Value=="CUTW") && ( Inputs.var_webpartnum.Value != "")) {Inputs.cmb_productgroup.Value = "Tensabarrier Post Range";}
  }
  
  
  if (Inputs.var_webpartsearch.Value == "11513") {
  if ((Inputs.var_webpartclass.Value=="CWEB") && ( Inputs.var_webpartnum.Value != "")) {Inputs.cmb_productgroup.Value = "Tensabright";}
  if ((Inputs.var_webpartclass.Value=="SWEB") && ( Inputs.var_webpartnum.Value != "")) {Inputs.cmb_productgroup.Value = "Tensabright";}
  if ((Inputs.var_webpartclass.Value=="CUTW") && ( Inputs.var_webpartnum.Value != "")) {Inputs.cmb_productgroup.Value = "Tensabright";}
  }
  
  
if (Inputs.cmb_productgroup.Value == "Tensamedia") {Inputs.cmb_coresolution.Value = "Solution";}
if (Inputs.cmb_productgroup.Value == "Tensabarrier Post Range") {Inputs.cmb_coresolution.Value = "Core";}
if (Inputs.cmb_productgroup.Value == "Tensabright") {Inputs.cmb_coresolution.Value = "Core";}
}


/* *************************************************************************************************************** */

decimal weblengtherrorcalc = 0m;
string weblengtherrormsg = "";


if ((Inputs.var_maxweblength.Value != "") && ( Inputs.dec_customweblength.Value >0)) {
  weblengtherrorcalc = UDMethods.ConvertToDecimal(Inputs.var_maxweblength.Value) - Inputs.dec_customweblength.Value;
  weblengtherrormsg = "Vous avez sélectionné une longueur de sangle qui dépasse la longueur maximale de " + "(" + Inputs.var_maxweblength.Value + "m) définie pour la variante de sangle choisie. Sélectionnez une longueur plus courte.";

	MessageBox.Show(weblengtherrormsg, "Prendre Note...", MessageBoxButtons.OK,  MessageBoxIcon.Information);

}

Refresh.DynamicList("cmb_tapeendselection");




















