				bool available_part = false;


        if ((Inputs.cmb_coresolution.Value == "") || (Inputs.cmb_sumsalescat.Value == "") || (Inputs.cmb_sector.Value == ""))
        {
            Inputs.var_smartstringvalidity.Value = "** Prendre Note ** Vous n'avez pas repondu a toutes les points obligatoires ......";
						MessageBox.Show("Vous n'avez pas repondu a toutes les points obligatoires ......", "Prendre Note...", MessageBoxButtons.OK,  MessageBoxIcon.Information);
            PageLeaveFunctions.SetNextInputPage(1);
						Args.Cancel = true;
        }
        else if ((Inputs.cmb_coresolution.Value != "") && (Inputs.cmb_sector.Value != ""))
        {

            /*  -------------------------------------------------------------------------------------------------------------------------------------------- */

            /* Consolidates webbing if Dualline selection matches top cassette */
            if (Inputs.var_topwebpart.Value == Inputs.var_botwebpart.Value)
            {
                Inputs.var_webpartnum.Value = Inputs.var_topwebpart.Value;
                Inputs.var_dualwebpartnum.Value = "";

                Inputs.var_webqty.Value = Inputs.var_topwebqty.Value + Inputs.var_botwebqty.Value;
                Inputs.var_dualwebqty.Value = 0;
            }

            if (Inputs.var_topwebpart.Value != Inputs.var_botwebpart.Value)
            {
                Inputs.var_webpartnum.Value = Inputs.var_topwebpart.Value;
                Inputs.var_dualwebpartnum.Value = Inputs.var_botwebpart.Value;

                Inputs.var_webqty.Value = Inputs.var_topwebqty.Value;
                Inputs.var_dualwebqty.Value = Inputs.var_botwebqty.Value;
            }

            /*  -------------------------------------------------------------------------------------------------------------------------------------------- */

            /* Clears all Base parts from the BOM if No Base is selected after initially selecting an actual base */

            if (Inputs.cmb_baseselection.Value == "Pas de base")
