                Inputs.var_basepartnum.Value = "";
                Inputs.var_basecoverpartnum.Value = "";
                Inputs.var_baseprotectorpartnum.Value = "";
                Inputs.var_basekitpartnum.Value = "";
                Inputs.var_basepackingpartnum.Value = "";
                Inputs.var_baseqty.Value = 0;
                Inputs.var_basecoverqty.Value = 0;
                Inputs.var_baseprotectorqty.Value = 0;
            }

