            /* Determines if a Cassette operation is required */

            if ((Inputs.var_cassettepartnum.Value != "") || (Inputs.var_dualcasspartnum.Value != "")) { Inputs.var_cassoprqd.Value = "TRUE"; }
            if ((Inputs.var_cassettepartnum.Value == "") && (Inputs.var_dualcasspartnum.Value == "")) { Inputs.var_cassoprqd.Value = "FALSE"; }

            /*  -------------------------------------------------------------------------------------------------------------------------------------------- */

            /* Smart Code String */


            if ((Inputs.cmb_postselection.Value == "Stowaway SA1") && (Inputs.rbn_stowawayqty.Value == "1")) { Inputs.var_fgnum.Value = Inputs.var_smartcodepost.Value + "1"; }
            if ((Inputs.cmb_postselection.Value == "Stowaway SA1") && (Inputs.rbn_stowawayqty.Value == "2")) { Inputs.var_fgnum.Value = Inputs.var_smartcodepost.Value + "4"; }
            if (Inputs.cmb_postselection.Value != "Stowaway SA1") { Inputs.var_fgnum.Value = Inputs.var_smartcodepost.Value; }

            if (Inputs.chk_duallineoption.Value == true) { fgdualoption = "T2"; }


            /* *********************************************************************************** */

            /* Calculation of OrderDtl_ProdCode based on webbing variant selected */
            /* Standard Webbing Variants */
            if ((Inputs.cmb_webvar.Value == "Standard") || (Inputs.cmb_webvar.Value == ""))
            {
                if (Inputs.cmb_productgroup.Value == "Tensabarrier Post Range") { prodgroup = "TBR"; }
                if (Inputs.cmb_productgroup.Value == "Tensamedia") { prodgroup = "TME"; }
                if (Inputs.cmb_productgroup.Value == "Tensabright") { prodgroup = "BRI"; }
                if (Inputs.cmb_coresolution.Value == "Core") { coretype = "COR"; }
                if (Inputs.cmb_coresolution.Value == "Solution") { coretype = "SOL"; }

                /* Smart String Segment 5 Cassette Type Ref */
                weblength1 = Inputs.chr_webbingtype.Value;
            }


						if (Inputs.cmb_webvar.Value != "Standard")
						{
						
                /* Not Custom Webbing */
                if (Inputs.chk_customwebbing.Value == false)
                {
                    prodgroup = PCLookUp.DataLookup("WebbingVariants", "std_prodgroup", Inputs.cmb_webvar.Value);
                    coretype = PCLookUp.DataLookup("WebbingVariants", "std_salecat", Inputs.cmb_webvar.Value);
                }
                if (Inputs.chk_customwebbing.Value == true)
                {
                    prodgroup = PCLookUp.DataLookup("WebbingVariants", "custom_prodgroup", Inputs.cmb_webvar.Value);
                    coretype = PCLookUp.DataLookup("WebbingVariants", "custom_salecat", Inputs.cmb_webvar.Value);
                }

                /* Smart String Segment 5 Cassette Type Ref */
                if (Inputs.chr_webbingtype.Value == "STD")
                {
                    weblength1 = PCLookUp.DataLookup("WebbingVariants", "STD_smartref", Inputs.cmb_webvar.Value);
                }

                if (Inputs.chr_webbingtype.Value == "MAX")
                {
                    weblength1 = PCLookUp.DataLookup("WebbingVariants", "MAX_smartref", Inputs.cmb_webvar.Value);
                }

                if (Inputs.chr_webbingtype.Value == "WDE")
                {
                    weblength1 = PCLookUp.DataLookup("WebbingVariants", "WDE_smartref", Inputs.cmb_webvar.Value);
                }

                if (Inputs.chr_webbingtype.Value == "RCV")
                {
                    weblength1 = PCLookUp.DataLookup("WebbingVariants", "RCV_smartref", Inputs.cmb_webvar.Value);
                }
						
						}


            if (Inputs.chr_webbingtype.Value == "") { weblength1 = "X"; }

            /* Top Cassette ID */
            string smartcassid = "";
            if (Inputs.var_smartcassid.Value != "") { smartcassid = Inputs.var_smartcassid.Value; }
            if (Inputs.var_smartcassid.Value == "") { smartcassid = ""; }

            /* Dual Cassette ID */
            string smartcassiddual = "";
            if (Inputs.var_smartcassiddual.Value != "") { smartcassiddual = Inputs.var_smartcassiddual.Value; }
            if (Inputs.var_smartcassiddual.Value == "") { smartcassiddual = ""; }

            /* ********************************************************* */

            /* Webbing Length */
            string smartweblength = "";
            string smartweblength1 = "";
            string smartweblengthdual = "";

            /*if ((Inputs.var_iscustomlength.Value == "TRUE") && (Inputs.dec_customweblength.Value < 10.00)) { smartweblength1 = String(Inputs.dec_customweblength.Value, "9.99"); }
            if ((Inputs.var_iscustomlength.Value == "TRUE") && (Inputs.dec_customweblength.Value > 10.00) && (Inputs.dec_customweblength.Value < 100.00)) { smartweblength1 = String(Inputs.dec_customweblength.Value, "99.99"); }*/

						if (Inputs.var_iscustomlength.Value == "TRUE")
						{
							smartweblength1 = Inputs.dec_customweblength.Value.ToString("F");
						}

            if (Inputs.var_iscustomlength.Value != "TRUE") { smartweblength1 = Inputs.cmb_webbinglength.Value; }

            /* ********************************************************* */

            /* Top Cassette */
            smartweblength = smartcassid + weblength1 + smartweblength1;

            /* Dualline Cassette */
            if (Inputs.cmb_dualcassetteselection.Value != "") { smartweblengthdual = smartcassiddual + weblength1 + smartweblength1; }
            if (Inputs.cmb_dualcassetteselection.Value == "") { smartweblengthdual = "X"; }

            /* *********************************************************************************** */

            if (Inputs.var_smartcodetapeend.Value != "") { tapeend = Inputs.var_smartcodetapeend.Value; }
            if (Inputs.var_smartcodetapeend.Value == "") { tapeend = "X"; }

            /* ********************************************************* */

            /* Determine webbing finish based on selection or entry */
            if (Inputs.chk_websuffixentry.Value == false)
            {
                if (Inputs.cmb_webbingfinish.Value != "") { webcol = Inputs.cmb_webbingfinish.Value.Entry(1, "-"); }
                if (Inputs.cmb_webbingfinish.Value == "") { webcol = "X"; }
            }

            if ((Inputs.chk_websuffixentry.Value == true) && (Inputs.var_websuffixentry.Value != ""))
            {
                webcol =  Inputs.var_websuffixentry.Value.ToUpper();
            }

            if ((Inputs.chk_websuffixentry.Value == true) && (Inputs.var_websuffixentry.Value == ""))
            {
                if (Inputs.cmb_webbingfinish.Value != "") { webcol = Inputs.cmb_webbingfinish.Value.Entry(1, "-"); }
                if (Inputs.cmb_webbingfinish.Value == "") { webcol = "X"; }
            }

            /* ********************************************************* */
            /* Base Finish */
            string smartbasefinish = "";
            smartbasefinish = "";

            if (Inputs.var_smartcodebasefinish.Value == "")
            {
                if (Inputs.var_basepartnum.Value != "") { smartbasefinish = Inputs.var_basepartnum.Value.Entry(1, "-"); }
                if (Inputs.var_basepartnum.Value == "") { smartbasefinish = ""; }
            }

            if (Inputs.var_smartcodebasefinish.Value != "")
            {
                smartbasefinish = Inputs.var_smartcodebasefinish.Value;
            }

            /* ********************************************************* */

            if (Inputs.var_dualwebtype.Value != "") { weblength2 = Inputs.var_dualwebtype.Value; }
            if (Inputs.var_dualwebtype.Value == "") { weblength2 = "X"; }

            /* ********************************************************* */

            /* Determine webbing finish based on selection or entry */
            if (Inputs.chk_websuffixentrydual.Value == false)
            {
                if (Inputs.cmb_dualwebbingfinish.Value != "") { webcol2 = Inputs.cmb_dualwebbingfinish.Value.Entry(1, "-"); }
                if (Inputs.cmb_dualwebbingfinish.Value == "") { webcol2 = "X"; }
            }

            if ((Inputs.chk_websuffixentrydual.Value == true) && (Inputs.var_websuffixentrydual.Value != ""))
            {
                webcol2 = Inputs.var_websuffixentrydual.Value.ToUpper();
            }

            if ((Inputs.chk_websuffixentrydual.Value == true) && (Inputs.var_websuffixentrydual.Value == ""))
            {
                if (Inputs.cmb_dualwebbingfinish.Value != "") { webcol2 = Inputs.cmb_dualwebbingfinish.Value.Entry(1, "-"); }
                if (Inputs.cmb_dualwebbingfinish.Value == "") { webcol2 = "X"; }
            }

            /* ********************************************************* */

            if (Inputs.var_smartcodedualtapeend.Value != "") { dualtapeend = Inputs.var_smartcodedualtapeend.Value; }
            if (Inputs.var_smartcodedualtapeend.Value == "") { dualtapeend = "X"; }

            /* ********************************************************* */

            /* Post & Post Head Finish References */
            if (Inputs.var_subpostfinish.Value == "")
            {
                if (Inputs.var_postpartnum.Value != "") { postcol = Inputs.var_postpartnum.Value.Entry(1, "-"); }
                if (Inputs.var_postpartnum.Value == "") { postcol = Inputs.var_postassybom.Value.Entry(1, "-"); }
            }

            if (Inputs.var_subpostfinish.Value != "")
            {
                if (Inputs.var_postpartnum.Value != "") { postcol = Inputs.var_postpartnum.Value.Entry(1, "-") + Inputs.var_subpostfinish.Value; }
                if (Inputs.var_postpartnum.Value == "") { postcol = Inputs.var_postassybom.Value.Entry(1, "-") + Inputs.var_subpostfinish.Value; }
            }

            /* ********************************************************* */

            if (Inputs.var_smartcoveraspostfinish.Value != "TRUE")
            {
                if (Inputs.var_smartcodebasecover.Value != "") { coverfinish = Inputs.var_smartcodebasecover.Value; }
                if (Inputs.var_smartcodebasecover.Value == "") { coverfinish = "X"; }
            }

            if (Inputs.var_smartcoveraspostfinish.Value == "TRUE")
            {
                coverfinish = postcol;
            }

            /* ********************************************************* */

            if (Inputs.var_smartprotectoroverride.Value == "")
            {
                if (Inputs.var_smartcodeprotectors.Value != "") { protector = Inputs.var_smartcodeprotectors.Value; }
                if ((Inputs.var_smartcodeprotectors.Value == "") || (Inputs.cmb_baseprotector.Value == "")) { protector = "X"; }
            }

            if (Inputs.var_smartprotectoroverride.Value != "")
            {
                protector = Inputs.var_smartprotectoroverride.Value;
            }

            /* ********************************************************* */

            if (Inputs.cmb_labelselect.Value != "") { topcaplabel = Inputs.cmb_labelselect.Value.Entry(1, "-"); }

            if ((Inputs.var_topcappartnum.Value == "") && (Inputs.var_padprintsuffix.Value != "") && (Inputs.cmb_labelselect.Value != "")) { topcaplabel = Inputs.var_padprintsuffix.Value; }

            if (Inputs.var_padprintsuffix.Value == "")
            {
                if (((Inputs.var_topcappartnum.Value == "") && (Inputs.cmb_labelselect.Value == "")) || (Inputs.chk_nolabel.Value == true)) { topcaplabel = "X"; }
            }

            /* ********************************************************* */

            string sumsalescat = "";

            if (Inputs.cmb_sumsalescat.Value == "Core") { sumsalescat = "COR"; }
            if (Inputs.cmb_sumsalescat.Value == "Solution") { sumsalescat = "SOL"; }

            Inputs.var_smartstring.Value = Inputs.var_fgnum.Value + fgdualoption + "|" + Inputs.var_smartcodebase.Value + smartbasefinish + "|" + coverfinish + "|" + postcol + "|" + smartweblength + "|" + webcol + "|" + tapeend + "|" + smartweblengthdual + "|" + webcol2 + "|" + dualtapeend + "|" + protector + "|" + topcaplabel;


            Inputs.var_smart1.Value = coretype + "-" + prodgroup;
            Inputs.var_smart2.Value = Inputs.var_fgnum.Value + fgdualoption + "+" + Inputs.var_smartcodebase.Value + smartbasefinish + coverfinish + postcol + smartweblength + webcol + tapeend + smartweblengthdual + webcol2 + dualtapeend + protector + topcaplabel;
            Inputs.var_smart3.Value = Inputs.var_sectorcode.Value;
            Inputs.var_sku.Value = Inputs.var_smart2.Value;


            /* Sets Final FG Number to display on screen and for determination of sales line description */
            Inputs.var_fgfinal.Value = Inputs.var_fgnum.Value + fgdualoption;

            /* **************************************************************************************************************** */

            /* Cassette Sub Assembly Smart ID */
            Inputs.var_casssmart.Value = "";

            if (Inputs.var_topcapincluded.Value != "TRUE")
            {
                /* TopCap label supplied separately */
                if (Inputs.var_casfgnum.Value != "") { Inputs.var_casssmart.Value = Inputs.var_casfgnum.Value + "+" + "X" + "X" + "X" + smartweblength + webcol + tapeend + "X" + "X" + "X" + "X" + "X"; }
            }

            if (Inputs.var_topcapincluded.Value == "TRUE")
            {
                /* TopCap label supplied included */
                if (Inputs.var_casfgnum.Value != "") { Inputs.var_casssmart.Value = Inputs.var_casfgnum.Value + "+" + "X" + "X" + "X" + smartweblength + webcol + tapeend + "X" + "X" + "X" + "X" + topcaplabel; }
            }

            if (Inputs.var_casfgnum.Value == "") { Inputs.var_casssmart.Value = ""; }

            Inputs.var_cassettepartnum.Value = Inputs.var_casssmart.Value;
            if (Inputs.var_cassettepartnum.Value != "") { Inputs.var_cassetteqty.Value = 1; }
            if (Inputs.var_cassettepartnum.Value == "") { Inputs.var_cassetteqty.Value = 0; }

            /* **************************************************************************************************************** */

            /* Determine if a Part Record exists for the FG Assy and return the part desc */
            string partdescfound = "";
						string Description = "";
            available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.var_sku.Value, out Description);

            if (available_part) {
                partdescfound = Description;
            }

            if (partdescfound == "") { Inputs.var_fgpartdesc.Value = ""; }
            if (partdescfound != "") { Inputs.var_fgpartdesc.Value = partdescfound; }

            /* *********************************************** */

            /*FG description and number String generation*/
            string csvfgdesc = "";
            csvfgdesc = "";
            Inputs.var_fgdesc.Value = "";
            Inputs.var_fgvalidity.Value = "";

            /* Determines if an alternate description string is required */
            string altdescrqd = "";
            altdescrqd = "";
            altdescrqd = PCLookUp.DataLookup("TBPCassetteRange", "fg_desc_type", Inputs.cmb_cassetteselection.Value);

            if (altdescrqd == "")
            {
                csvfgdesc = PCLookUp.DataLookup("TENUK_FG_MasterTable", "fg_desc", Inputs.var_fgfinal.Value);
            }

            if (altdescrqd != "")
            {
                csvfgdesc = PCLookUp.DataLookup("TENUK_FG_MasterTable", altdescrqd, Inputs.var_fgfinal.Value);
            }

            if (Inputs.var_fgpartdesc.Value == "") { Inputs.var_fgdesc.Value = csvfgdesc; }
            if (Inputs.var_fgpartdesc.Value != "") { Inputs.var_fgdesc.Value = Inputs.var_fgpartdesc.Value; }


            if (Inputs.var_fgfinal.Value == "") { Inputs.var_fgvalidity.Value = "** Erreur ** Numéro de produits finis non trouvé."; /* ** ERROR ** No Finished Goods number found. */ }
            if ((Inputs.var_fgdesc.Value == "") && (Inputs.var_fgfinal.Value != "")) { Inputs.var_fgvalidity.Value = "Il n'y a aucune enregistrement des produits finis dans la table TENUK_FG_MasterTable."; }
						/* ** ERROR ** The Finished Goods number has no TENUK_FG_MasterTable record. */
            if ((Inputs.var_fgdesc.Value != "") && (Inputs.var_fgfinal.Value != "")) { Inputs.var_fgvalidity.Value = "OK"; }

            /* Set standard Warranty Code */
            string warranty = "";
            warranty = PCLookUp.DataLookup("TENUK_FG_MasterTable", "std_warranty", Inputs.var_fgnum.Value);
            if (warranty == "") { Inputs.chr_warranty.Value = "2YR"; }
            if (warranty != "") { Inputs.chr_warranty.Value = warranty; }



            /* Error if no Stowaway Packing Qty is selected */
            if (((Inputs.cmb_postselection.Value == "Stowaway SA1") && (Inputs.rbn_stowawayqty.Value == "")))
            {/*  Please select a packing qty for the Stowaway Post (Singular or a Four Pack). */
								MessageBox.Show("S'il vous plaît sélectionnez la quantité d'emballage pour le Stowaway Post (Un seul ou un pack de quatre)", "Prendre Note...", MessageBoxButtons.OK,  MessageBoxIcon.Information);
            }

            /* ************************************************************************************************************************************************************************************************* */

            /* Generate Comment Strings */

            string postcommenttype = "";
            string postduallinecommenttype = "";
            string postfinishcommenttype = "";
            string basecommenttype = "";
            string basecoverfinishcommenttype = "";
            string cass1commenttype = "";
            string webbingcommenttype = "";
            string tapeendcommenttype = "";

            /* ************************************* */

            Inputs.chk_overridecomments.Value = true;
            Inputs.chk_jobcomallow.Value = true;
            Inputs.chk_salescomallow.Value = true;
            Inputs.chk_packcomallow.Value = true;
            Inputs.chk_boxcomallow.Value = true;

            /* ************************************* */

            flatpack = "";

            /* Set the post comment variable */
            postcommenttype = PCLookUp.DataLookup("TBPPostSelection", "post_comment", Inputs.cmb_postselection.Value);

            /* Dualline or Single Line Post */
            if (Inputs.chk_duallineoption.Value == false) { postduallinecommenttype = ""; }
            if (Inputs.chk_duallineoption.Value == true) { postduallinecommenttype = " dualline"; }

            /* Removes the finish suffix reference from the post finish */
            postfinishcommenttype = PCLookUp.DataLookup("TBPPostFinish", "comment_string", Inputs.cmb_postfinishselection.Value);

            /* Base Protector comment variable */
            string engbaseprotectordesc = "";
            engbaseprotectordesc = PCLookUp.DataLookup("TBPBaseProtectors", "comment_string", Inputs.cmb_baseprotector.Value);
            /*if (engbaseprotectordesc == "") { ""; }*/
            /*if (engbaseprotectordesc != "") { engbaseprotectordesc; }*/

            /* Set the base comment variable */
            string engbasedesc = "";
            engbasedesc = PCLookUp.DataLookup("TBPBaseRange", "base_comment", Inputs.cmb_baseselection.Value);
            if (engbaseprotectordesc == "") { basecommenttype = engbasedesc; }
            if (engbaseprotectordesc != "") { basecommenttype = engbasedesc + " (" + engbaseprotectordesc + ")"; }

            /* Set the base cover finish comment variable */
            string engbasecoverdesc = "";
            string amendedrefbasecoverfinish = "";
            engbasecoverdesc = PCLookUp.DataLookup("TBPBaseFinish", "comment_string", Inputs.cmb_basecoverfinish.Value);
            amendedrefbasecoverfinish = engbasecoverdesc;

            /* Set the base cover comment variable */
            if ((Inputs.var_fgbasefilter.Value == "Fourni") && (Inputs.var_smartcodebase.Value == "U")) { basecoverfinishcommenttype = " avec " + postfinishcommenttype + " base cover (couvrir au fond), "; }
            if ((Inputs.var_fgbasefilter.Value != "Fourni") && (Inputs.cmb_basecoverfinish.Value == "")) { basecoverfinishcommenttype = ", "; }
            if ((Inputs.var_fgbasefilter.Value != "Fourni") && (Inputs.cmb_basecoverfinish.Value != "")) { basecoverfinishcommenttype = " avec" + amendedrefbasecoverfinish + " base cover (couvrir au fond), "; }

            /* Set the top cassette comment variable */
            string cass1interimcomment = "";
            cass1interimcomment = PCLookUp.DataLookup("TBPCassetteRange", "comment_string", Inputs.cmb_cassetteselection.Value);
            if (cass1interimcomment == "") { cass1commenttype = "Pas de Cassette"; }
            if (cass1interimcomment != "") { cass1commenttype = cass1interimcomment; }


            /* Set the webbing comment variable */
            string webcommentdesc = "";
            if ((Inputs.cmb_webbinglength.Value != "") && (Inputs.cmb_webbingfinish.Value != ""))
            {
                if ((Inputs.chk_websuffixentry.Value == false) && (Inputs.cmb_webbingfinish.Value != "")) { webcommentdesc = Inputs.cmb_webbingfinish.Value.ToUpper() + " webbing/sangle"; }
                if ((Inputs.chk_websuffixentry.Value == true) && (Inputs.cmb_webbingfinish.Value != "")) { webcommentdesc = Inputs.var_websuffixentry.Value.ToUpper() + " webbing/sangle"; }
            }
            webbingcommenttype = " avec " + smartweblength1 + "m de " + webcommentdesc;

            /* Set the Tape End comment variable */
            string engtapeenddesc = "";
            engtapeenddesc = PCLookUp.DataLookup("TBPTapeEndOptions", "comment_string", Inputs.cmb_tapeendselection.Value);
            if (Inputs.cmb_tapeendselection.Value != "") { tapeendcommenttype = ", fitted with a/équipé d'un " + engtapeenddesc + " tape end/fin de bande. "; }

            string engtopcapdesc = "";
            if (Inputs.chk_nolabel.Value == true) { engtopcapdesc = "No TopCap Label/Pas d'etiquette 'TopCap'."; }

            if ((Inputs.cmb_labelselect.Value != "") && (Inputs.chk_nolabel.Value == false))
            {
								string desc = "";
                available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.cmb_labelselect.Value, out desc);

                if (available_part) {
                    engtopcapdesc = desc;
                }
            }

            /* Comment string generation as viewed on Page 4. */
            /*
            Inputs.var_linecomment.Value = postcommenttype + LC(postduallinecommenttype) + " post in "  + LC(postfinishcommenttype) + ", " + LC(basecommenttype) + " base" + LC(basecoverfinishcommenttype) + LC(cass1commenttype) + LC(webbingcommenttype) + LC(tapeendcommenttype);
            */


            interimpocomment = flatpack + postcommenttype + postduallinecommenttype.ToLower() + " post in " + postfinishcommenttype.ToLower() + ", " + basecommenttype.ToLower() + " base" + basecoverfinishcommenttype.ToLower() + cass1commenttype .ToLower() + webbingcommenttype + tapeendcommenttype.ToLower() + " " + engtopcapdesc;


            
            interimcasspocomment = cass1commenttype + webbingcommenttype +  tapeendcommenttype.ToLower() + " " + engtopcapdesc;


            /* ******************************************************************************************************************************************************************************************************** */

            if (Inputs.var_postpartnum.Value == "") { Inputs.var_postqty.Value = 0; }

            /* To be returned to the notes line on the label */

            string labelpostfinish = "";
            string labelbasecoverfinish = "";
            string labelwebbingfinish = "";

            if (Inputs.cmb_postfinishselection.Value != "") { labelpostfinish = Inputs.cmb_postfinishselection.Value + " post finish (la finition post), "; }
            if (Inputs.cmb_basecoverfinish.Value != "") { labelbasecoverfinish = Inputs.cmb_basecoverfinish.Value + " cover finish (la finition couverture),"; }
            if (Inputs.cmb_webbingfinish.Value != "")
            {
								string partdesc = "";
                /* Determines webbing description for use in comment string */

                available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.var_webpartnum.Value, out partdesc);

                if (available_part) {
                    labelwebbingfinish = partdesc;
                }
            }

            Inputs.var_labeldesc.Value = labelpostfinish + labelbasecoverfinish + labelwebbingfinish;

            /* Validity Routines */

            /* Checks if FG Number passed to sales line is less than 50 Characters */
            decimal smartcount = 0m;
            smartcount = 0;
            smartcount = Inputs.var_smart2.Value.Length;
            if (smartcount > 50)
            {
								MessageBox.Show("Le nombre de produits finis depasse le nombre maximal de caracteres autorises dans le champ Numero de piece de la ligne de vente ......", "Erreur", MessageBoxButtons.OK,  MessageBoxIcon.Exclamation);

                PageLeaveFunctions.SetNextInputPage(1);
								Args.Cancel = true;
            }

            /* Checks if fg description has been passed */
            string fgdesccheck = "";
            string fgdesc = "";
            fgdesccheck = Inputs.var_fgdesc.Value;

            if (fgdesccheck == "")
            {
								string dsc = "";
                available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.var_fgfinal.Value, out dsc);

                if (available_part) {
                    fgdesc = dsc;
                }
                if (fgdesc != "")
                {
                    Inputs.var_fgdesc.Value = fgdesc;
                    Inputs.var_fgvalidity.Value = "OK";
                }
                if ((fgdesc == "") && (Inputs.chk_erroron.Value == true))
                {
										MessageBox.Show("Aucune description n'a ete retournee pour le numero de produit fini .........", "Erreur", MessageBoxButtons.OK,  MessageBoxIcon.Exclamation);

                    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;
                }
            }

            /* **************************************************************************************************************************************************************** */

            /* **************************************************************************************************************************************************************** */


            /* Returns Commodity Code from CSV */
            Inputs.var_commodity.Value = PCLookUp.DataLookup("TENUK_FG_MasterTable", "commodity_code", Inputs.var_fgfinal.Value);

            /* If no commodity code is returned from the CSV file it checks the parts master table */
            string partclass = "";

            if (Inputs.var_commodity.Value == "")
            {
								string clsid = "";
                available_part = UDMethods.GetPartClass(Context.CompanyID, Inputs.var_fgfinal.Value, out clsid);

                if (available_part) {
                    partclass = clsid;
                }
            }
            /* As Part Class is used to assign Commodity Code */
            if ((Inputs.var_commodity.Value == "") && (partclass != ""))
            {
								string PartClassCommodityCode = "";
                bool available_partclass = UDMethods.GetPartClassCommodity(Context.CompanyID, partclass, out PartClassCommodityCode);

                if (available_partclass) {
                    Inputs.var_commodity.Value = PartClassCommodityCode;
                }
            }

            /* Verifies Commodity code exists in IComCode Table within Epicor */
            string commoditycheck = "";
            string ICommCodeImportCode = "";
            if (Inputs.var_commodity.Value != "")
            {
                bool available_ICommCode = UDMethods.GetICommImportCode(Context.CompanyID, Inputs.var_commodity.Value, out ICommCodeImportCode);

                if (available_ICommCode) {
                    commoditycheck = ICommCodeImportCode;
                }
            }

            if ((Inputs.var_commodity.Value != commoditycheck) && (Inputs.chk_erroron.Value == true))
            {

								MessageBox.Show("Le configurateur a attribue un code Commodity qui n'a pas d'enregistrement Epicor correspondant, contactez le service Operations pour corriger cette erreur.", "Erreur", MessageBoxButtons.OK,  MessageBoxIcon.Exclamation);

                PageLeaveFunctions.SetNextInputPage(1);
								Args.Cancel = true;
            }

            if ((Inputs.var_commodity.Value == "") && (Inputs.chk_erroron.Value == true))
            {
						MessageBox.Show("Le configurateur n'a pas pu determiner le code de marchandise pour la configuration choisie. Veuillez contacter le service des operations.", "Erreur", MessageBoxButtons.OK,  MessageBoxIcon.Exclamation);

                PageLeaveFunctions.SetNextInputPage(1);
								Args.Cancel = true;
            }
            else if ((Inputs.var_commodity.Value != "") || (Inputs.var_commodity.Value == commoditycheck))
            {
                PageLeaveFunctions.SetNextInputPage(4);
            }

            /* ********************************************************************************************************************************************************************************** */

            /* Pack Comment */

						int CustID = 0;
						int OrderNum = 0;
						int QuoteNum = 0;
						string CustCountry = "";
						string CustName = "";
						string OTSID = "";
						bool OTS = false;
						int OTSCountry = 0;
						
						/* Determines Current Configuration Mode and order/quote specifics */
						string Mode = Context.Entity;
						CustID = 0; /* Set to Zero when not used for testing*/
						CustCountry = "";
						OrderNum = Context.OrderNumber;
						QuoteNum = Context.QuoteNumber;
						string currencyCode = "";

            /* ********************************************************************************************************************************************************************************** */
            if (Mode == "Quote")
            {
									/* Retrieve details from Quote Header */
									bool available_QuoteHed = UDMethods.GetQuoteHed(Context.CompanyID, QuoteNum, out CustID, out OTSID, out OTS, out currencyCode);
							
							    if (available_QuoteHed)
							    {
							        Inputs.var_custid.Value = CustID;
							        //Inputs.var_custcurrency.Value = currencyCode;
							        //Inputs.var_custcurrency1.Value = currencyCode;
							    }
							
									/* Retrieves customer name using customer ID */
									bool available_Customer = UDMethods.GetCustomer(Context.CompanyID, CustID, out CustName, out CustCountry, out currencyCode);
									
									/* If a saved shipto address is specified gets country info */
									if (OTSID != "")
									{
									    bool available_ShipTo = UDMethods.GetShipToCountryNum(Context.CompanyID, OTSID, out OTSCountry);
									    bool available_Country = UDMethods.GetCountryDescription(Context.CompanyID, OTSCountry, out CustCountry);
									}
							
								  Inputs.var_orderdtl.Value = "Mode: " + Mode + "  |  Order/Quote No: " + QuoteNum.ToString() + "   | Cust: " + CustName + "  |   ShipCountry: " + CustCountry;
								  Inputs.var_shipcountry.Value = CustCountry;
						}

            /* ********************************************************************************************************************************************************************************** */
            if (Mode == "Order")
            {

								/* Retrieve details from Order Header */
					      bool available_OrderHed = UDMethods.GetOrderHed(Context.CompanyID, OrderNum, out CustID, out OTSID, out OTS, out currencyCode);
					
					      if (available_OrderHed)
					      {
					        Inputs.var_custid.Value = CustID;
					        //Inputs.var_custcurrency.Value = currencyCode;
					        //Inputs.var_custcurrency1.Value = currencyCode;
					      }
					
					      /* Retrieves customer name using customer ID */
					      bool available_Customer = UDMethods.GetCustomer(Context.CompanyID, CustID, out CustName, out CustCountry, out currencyCode);
					
					      /* If a saved shipto address is specified gets country info */
					      if (OTSID != "")
					      {
					          bool available_ShipTo = UDMethods.GetShipToCountryNum(Context.CompanyID, OTSID, out OTSCountry);
										bool available_Country = UDMethods.GetCountryDescription(Context.CompanyID, OTSCountry, out CustCountry);
					      }
					
							 Inputs.var_orderdtl.Value = "Mode: " + Mode + "  |  Order/Quote No: " + OrderNum.ToString() + "   | Cust: " + CustName + "  |   ShipCountry: " + CustCountry;
							 Inputs.var_shipcountry.Value = CustCountry;
						}

