// /* Inputs.var_smartstringvalidity.Value="OK". */
// 
// /* **************************************************************** */
// 
// /* 
// 
// if Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record." and Inputs.chk_erroron.Value=true then do:
// {lib/PublishEx.i &ExMsg = '"Un composant de la nomenclature n existe pas dans la table maitres des pieces ou l enregistrement est inactif. Veuillez contacter le service Operations pour resoudre ce probleme."'} {&THROW_PRIVATE}. 
// End.
// 
// */
// 
// /* *********************************************** */
// 
// /* Set the default release mode flag for Inputs.chk_rework.Value calculation */
// if Inputs.var_postreldefault.Value = "pick" or Inputs.var_basereldefault.Value = "pick" or Inputs.chk_duallineoption.Value = TRUE then Inputs.var_reldefault.Value = "pick".
// if Inputs.var_postreldefault.Value <> "pick" and Inputs.var_basereldefault.Value <> "pick" then Inputs.var_reldefault.Value = "make".
// 
// 
// if Inputs.var_bomstatus.Value = "" then Inputs.var_bomvalidity.Value = "OK".
// if Inputs.var_bomstatus.Value <> "" then Inputs.var_bomvalidity.Value = "Please review BOM Listing".
// 
// 
// /* ********************************************************************************************************************************************* */
// 
// /* Generate Job Mtl desc in English as a translation of the smart string format */
// 
// Inputs.var_jobmtlpureng.Value = interimpocomment.
// Inputs.var_jobmtlpurcasseng.Value = interimcasspocomment.
// 
// 
//

  decimal qtyonhand = 0m;
  decimal demandqty = 0m;
  decimal availqty = 0m;
  bool fromstock = false;

/* Determine the FG mode to operate in */

/* Set the BOM value visibility */
Inputs.var_jobmtlfgassy.Invisible = false;
Inputs.var_postpartnum.Invisible = false;
Inputs.var_postassybom.Invisible = false;
Inputs.var_basepartnum.Invisible = false;
Inputs.var_basecoverpartnum.Invisible = false;
Inputs.var_basekitpartnum.Invisible = false;
Inputs.var_baseprotectorpartnum.Invisible = false;
Inputs.var_topcappartnum.Invisible = false;
Inputs.var_cassettepartnum.Invisible = false;
Inputs.var_postqty.Invisible = false;
Inputs.var_postassyqty.Invisible = false;
Inputs.var_baseqty.Invisible = false;
Inputs.var_basecoverqty.Invisible = false;
Inputs.var_basekitqty.Invisible = false;
Inputs.var_baseprotectorqty.Invisible = false;
Inputs.var_topcapqty.Invisible = false;
Inputs.var_cassetteqty.Invisible = false;
/*Inputs.var_ver1.Invisible = false;*/
/*Inputs.var_ver22.Invisible = false;*/
/*Inputs.var_ver8.Invisible = false;*/
/*Inputs.var_ver9.Invisible = false;*/
/*Inputs.var_ver21.Invisible = false;*/
/*Inputs.var_ver12.Invisible = false;*/
/*Inputs.var_ver17.Invisible = false;*/
/*Inputs.var_ver13.Invisible = false;*/
Inputs.var_bomstatus.Invisible = false;

Inputs.var_jobmtlfgassy.Value = "";
Inputs.var_fgparttype.Value = "";

string assystockmodelookup = "";
assystockmodelookup = PCLookUp.DataLookup("TBPAssembly_Stocking_Mode", "mode", "fgassy");

/* Calculate the bool available_qty for the finished assembly */
bool partrecordexists = false;
string partrecordtype = "";
string PartTypeCode = "";

bool available_part = UDMethods.GetPartTypeCode(Context.CompanyID,Inputs.var_sku.Value, out PartTypeCode);

if (available_part) {
  partrecordexists = true;
  partrecordtype = PartTypeCode;
}

if (partrecordexists == true) {
   
 Inputs.var_fgparttype.Value = partrecordtype;

 decimal OnHandQty = 0m;
 decimal DemandQty = 0m;
  
  /* Search for a FG Part record and calculate the bool available_qty's */
  bool available_PartQty = UDMethods.GetPartQty(Context.CompanyID,Inputs.var_smart2.Value, out OnHandQty, out DemandQty);

  if (available_PartQty) {
		qtyonhand = OnHandQty;
    demandqty = DemandQty;
    availqty = qtyonhand - demandqty;
  }
  
  /* Make Direct Mode Only */
  if (assystockmodelookup == "NONE") {
    Inputs.chk_rework.Value = true;
    Inputs.var_configtype.Value = "MFG";
  }
  
  
  /* only take fg assy from stock if the bool available_qty fulfills the full demand */
  if (assystockmodelookup == "FULL") {
  if (availqty < Inputs.dec_soqty.Value) {fromstock = false;}
  if (availqty == Inputs.dec_soqty.Value) {fromstock = true;}
  if (availqty > Inputs.dec_soqty.Value) {fromstock = true;}
    
    
    /* Manufactured Part Decision */
    if (partrecordtype == "M") {
    if (fromstock == true) {Inputs.chk_rework.Value = false;}
    if (fromstock == false) {Inputs.chk_rework.Value = true;}
      /* Determine Config Type */
    if (fromstock == true) {Inputs.var_configtype.Value = "M-STK";}
    if (fromstock == false) {Inputs.var_configtype.Value = "MFG";}
    }
    
    if (partrecordtype == "P") {
    if (fromstock == true) {Inputs.chk_rework.Value = false;}
    if (fromstock == false) {Inputs.chk_rework.Value = false; /* Rqd due to a bug in CTP which ignores the sales release if a Purchased part record exists */}
      /* Determine Config Type */
      Inputs.var_configtype.Value = "PUR";
    }
    
    
    /* will take fg assy from stock even if the bool available_qty does not fulfill the full demand */
    if (assystockmodelookup == "ANY") {
    if (availqty >1) {fromstock = true;}
    if (availqty ==0) {fromstock = false;}
      
      /* Manufactured Part Decision */
      if (partrecordtype == "M") {
      if (fromstock == true) {Inputs.chk_rework.Value = false;}
      if (fromstock == false) {Inputs.chk_rework.Value = true;}
        /* Determine Config Type */
      if (fromstock == true) {Inputs.var_configtype.Value = "M-STK";}
      if (fromstock == false) {Inputs.var_configtype.Value = "MFG";}
      }
      
      if (partrecordtype == "P") {
      if (fromstock == true) {Inputs.chk_rework.Value = false;}
      if (fromstock == false) {Inputs.chk_rework.Value = false; /* Rqd due to a bug in CTP which ignores the sales release if a Purchased part record exists */}
        /* Determine Config Type */
        Inputs.var_configtype.Value = "PUR";
      }
    }
  }
}


if (partrecordexists == false) {
  Inputs.chk_rework.Value = true;
  /* Determine Config Type */
  Inputs.var_configtype.Value = "MFG";
}

/* String to indicate on Last Page that the fg assy will come from a stocked part */

if (Inputs.chk_rework.Value == false) {
  string assystring = "";
  assystring = "Assy: " + Inputs.dec_soqty.Value.ToString() + " x " + Inputs.var_smart2.Value + " will be taken from Stock.";



	Inputs.var_fgvalidity.Value = assystring;
	Inputs.var_fgvalidity.ToolTip = availqty.ToString();
	Inputs.var_bomvalidity .Invisible = true;
}

if (Inputs.chk_rework.Value == true) {
  Inputs.var_fgvalidity.Value = "Make Direct";
	Inputs.var_fgvalidity.ToolTip = "0";
	Inputs.var_bomvalidity .Invisible = false;
}



if (((Inputs.var_reldefault.Value.IndexOf("pick")>-1) && (partrecordexists != true)) || ( (Inputs.chk_jobmtloverride.Value==true) )) {
  
  string assystringpickdefault = "";
  assystringpickdefault = "Assy: " + Inputs.dec_soqty.Value.ToString() + " x " + Inputs.var_smart2.Value + " to be supplied via Assy Job Mtl.";


  Inputs.var_bomvalidity.Value = assystringpickdefault;
  Inputs.var_fgvalidity.Value = "Make";
Inputs.var_jobmtlfgassy.Invisible = false;
Inputs.var_postpartnum.Invisible = true;
Inputs.var_postassybom.Invisible = true;
Inputs.var_basepartnum.Invisible = true;
Inputs.var_basecoverpartnum.Invisible = true;
Inputs.var_basekitpartnum.Invisible = true;
Inputs.var_baseprotectorpartnum.Invisible = true;
Inputs.var_topcappartnum.Invisible = true;
Inputs.var_cassettepartnum.Invisible = true;
Inputs.var_postqty.Invisible = true;
Inputs.var_postassyqty.Invisible = true;
Inputs.var_baseqty.Invisible = true;
Inputs.var_basecoverqty.Invisible = true;
Inputs.var_basekitqty.Invisible = true;
Inputs.var_baseprotectorqty.Invisible = true;
Inputs.var_topcapqty.Invisible = true;
Inputs.var_cassetteqty.Invisible = true;
/*Inputs.var_ver1.Invisible = false;*/
/*Inputs.var_ver22.Invisible = false;*/
/*Inputs.var_ver8.Invisible = false;*/
/*Inputs.var_ver9.Invisible = false;*/
/*Inputs.var_ver21.Invisible = false;*/
/*Inputs.var_ver12.Invisible = false;*/
/*Inputs.var_ver17.Invisible = false;*/
/*Inputs.var_ver13.Invisible = false;*/
Inputs.var_bomstatus.Invisible = true;
  Inputs.var_jobmtlfgassy.Value = Inputs.var_sku.Value;
  
}

if ((Inputs.var_reldefault.Value !="pick") && ( Inputs.chk_jobmtloverride.Value!=true)) {Inputs.var_jobmtlfgassy.Value = "";}


/* *************************************************************** */

        /* Calculates Net Weight */

        decimal CompWeight1a = 0m;
        decimal CompWeight2a = 0m;
        decimal CompWeight3a = 0m;
        decimal CompWeight4a = 0m;
        decimal CompWeight5a = 0m;
        decimal CompWeight6a = 0m;
        decimal CompWeight7a = 0m;
        decimal CompWeight8a = 0m;
        decimal CompWeight9a = 0m;
        decimal CompWeight10a = 0m;
        decimal CompWeight11a = 0m;
        decimal CompWeight12a = 0m;
        decimal CompWeight13a = 0m;
        decimal CompWeight14a = 0m;
        decimal CompWeight15a = 0m;
        decimal CompWeight16a = 0m;
        decimal CompWeight17a = 0m;
        decimal CompWeight18a = 0m;
        decimal CompWeight19a = 0m;
        decimal CompWeight20a = 0m;
        decimal CompWeight21a = 0m;
        decimal CompWeight22a = 0m;
        decimal CompWeight23a = 0m;
				decimal PartWeight = 0m;

				/* CompWeight1a */
        if (Inputs.var_postpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_postpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight1a = PartWeight * Inputs.var_postqty.Value;
        }

				/* CompWeight2a */
        if (Inputs.var_postpackingpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_postpackingpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight2a = PartWeight * Inputs.var_postpackingqty.Value;
        }

				/* CompWeight3a */
        if (Inputs.var_allenkeypartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_allenkeypartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight3a = PartWeight * Inputs.var_allenkeyqty.Value;
        }

				/* CompWeight4a */
        if (Inputs.var_reflectivepartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_reflectivepartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight4a = PartWeight * Inputs.var_reflectiveqty.Value;
        }

				/* CompWeight5a */
        if (Inputs.var_slugpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_slugpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight5a = PartWeight * Inputs.var_slugqty.Value;
        }

				/* CompWeight6a */
        if (Inputs.var_centrepacking.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_centrepacking.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight6a = PartWeight * Inputs.var_centrepackqty.Value;
        }

				/* CompWeight7a */
        if (Inputs.var_utilityringpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_utilityringpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight7a = PartWeight * Inputs.var_utilityringqty.Value;
        }

				/* CompWeight8a */
        if (Inputs.var_basepartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_basepartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight8a = PartWeight * Inputs.var_baseqty.Value;
        }

				/* CompWeight9a */
        if (Inputs.var_basecoverpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_basecoverpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight9a = PartWeight * Inputs.var_basecoverqty.Value;
        }

				/* CompWeight10a */
        if (Inputs.var_basekitpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_basekitpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight10a = PartWeight * Inputs.var_basekitqty.Value;
        }

				/* CompWeight11a */
        if (Inputs.var_basepackingpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_basepackingpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight11a = PartWeight * Inputs.var_basepackingqty.Value;
        }

				/* CompWeight12a */
        if (Inputs.var_popsboxpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_popsboxpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight12a = PartWeight * Inputs.var_popboxqty.Value;
        }

				/* CompWeight13a */
        if (Inputs.var_baseprotectorpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_baseprotectorpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight13a = PartWeight * Inputs.var_baseprotectorqty.Value;
        }

				/* CompWeight14a */
        if (Inputs.var_cassettepartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_cassettepartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight14a = PartWeight * Inputs.var_cassetteqty.Value;
        }

				/* CompWeight15a */
        if (Inputs.var_wedgepartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_wedgepartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight15a = PartWeight * Inputs.var_wedgeqty.Value;
        }

				/* CompWeight16a */
        if (Inputs.var_webpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_webpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight16a = PartWeight * Inputs.var_webqty.Value;
        }

				/* CompWeight17a */
        if (Inputs.var_tapeendpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_tapeendpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight17a = PartWeight * Inputs.var_tapeendqty.Value;
        }

				/* CompWeight18a */
        if (Inputs.var_topcappartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_topcappartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight18a = PartWeight * Inputs.var_topcapqty.Value;
        }

				/* CompWeight19a */
        if (Inputs.var_dualcasspartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_dualcasspartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight19a = PartWeight * Inputs.var_dualcassqty.Value;
        }

				/* CompWeight20a */
        if (Inputs.var_dualwebpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_dualwebpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight20a = PartWeight * Inputs.var_dualwebqty.Value;
        }

				/* CompWeight21a */
        if (Inputs.var_dualtapeendpartnum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_dualtapeendpartnum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight21a = PartWeight * Inputs.var_dualtapeendqty.Value;
        }

				/* CompWeight22a */
        if (Inputs.var_postassybom.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_postassybom.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight22a = PartWeight * Inputs.var_postassyqty.Value;
        }

				/* CompWeight23a */
        if (Inputs.var_resetfobassynum.Value != "")
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_resetfobassynum.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight23a = PartWeight * Inputs.var_resetfobassyqty.Value;
        }


        /* Totals the individual component weights */
        decimal interimweightcalc = 0m;
        interimweightcalc = CompWeight1a + CompWeight2a + CompWeight3a + CompWeight4a + CompWeight5a + CompWeight6a + CompWeight7a + CompWeight8a + CompWeight9a + CompWeight10a + CompWeight11a + CompWeight12a + CompWeight13a + CompWeight14a + CompWeight15a + CompWeight16a + CompWeight17a + CompWeight18a + CompWeight19a + CompWeight20a + CompWeight21a + CompWeight22a + CompWeight23a;





        if ((interimweightcalc > 0.01m) && (Inputs.var_reldefault.Value != "pick"))
        {
						Inputs.var_netweight.Value = interimweightcalc;
            /* Used to display Netweight list incase verification is required */
 Inputs.var_weightlookup.Value = "1(" + CompWeight1a.ToString() + ")" + " - 2(" + CompWeight2a.ToString() + ")" + " - 3(" + CompWeight3a.ToString() + ")" + " - 4(" + CompWeight4a.ToString() + ")" + " - 5(" + CompWeight5a.ToString() + ")" + " - 6(" + CompWeight6a.ToString() + ")" + " - 7(" + CompWeight7a.ToString() + ")" + " - 8(" + CompWeight8a.ToString() + ")" + " - 9(" + CompWeight9a.ToString() + ")" + " - 10(" + CompWeight10a.ToString() + ")" + " - 11(" + CompWeight11a.ToString() + ")" + " - 12(" + CompWeight12a.ToString() + ")" + " - 13(" + CompWeight13a.ToString() + ")" + " - 14(" + CompWeight14a.ToString() + ")" + " - 15(" + CompWeight15a.ToString() + ")" + " - 16(" + CompWeight16a.ToString() + ")" + " - 17(" + CompWeight17a.ToString() + ")" + " - 18(" + CompWeight18a.ToString() + ")" + " - 19(" + CompWeight19a.ToString() + ")" + " - 20(" + CompWeight20a.ToString() + ")" + " - 21(" + CompWeight21a.ToString() + ")" + " - 22(" + CompWeight22a.ToString() + ")" + " - 23(" + CompWeight23a.ToString() + ")";

        }

        /* Sets a default value to prevent Intrastat Issues */
        if ((interimweightcalc > 0.01m) && (Inputs.var_reldefault.Value == "pick"))
        {
            string defaultintrastatvalue = "";
            //defaultintrastatvalue = PCLookUp.DataLookup("TENUK_FG_MasterTable", "intrastat_unitweightkg", Inputs.var_fgfinal.Value);
						defaultintrastatvalue = PCLookUp.DataLookup("TENFR_FG_MasterTable", "intrastat_unitweightkg", Inputs.var_fgfinal.Value);
            if (defaultintrastatvalue != "") { Inputs.var_netweight.Value = UDMethods.ConvertToDecimal(defaultintrastatvalue); }
            if (defaultintrastatvalue == "") { Inputs.var_netweight.Value = 1; }
        }


        /* Sets a default value to prevent Intrastat Issues */
        if (interimweightcalc == 0.00m)
        {
            string defaultintrastatvalue1 = "";
            //defaultintrastatvalue1 = PCLookUp.DataLookup("TENUK_FG_MasterTable", "intrastat_unitweightkg", Inputs.var_fgfinal.Value);
						defaultintrastatvalue1 = PCLookUp.DataLookup("TENFR_FG_MasterTable", "intrastat_unitweightkg", Inputs.var_fgfinal.Value);
            if (defaultintrastatvalue1 != "") { Inputs.var_netweight.Value = UDMethods.ConvertToDecimal(defaultintrastatvalue1); }
            if (defaultintrastatvalue1 == "") { Inputs.var_netweight.Value = 1; }
        }











