// if p1mtlpart7 <> "" and p1mtlpart7fifo=0 then P1CostChk = True.
// if p1mtlpart8 <> "" and p1mtlpart8fifo=0 then P1CostChk = True.
// if p1mtlpart9 <> "" and p1mtlpart9fifo=0 then P1CostChk = True.
// if p1mtlpart10 <> "" and p1mtlpart10fifo=0 then P1CostChk = True.
// if p1mtlpart11 <> "" and p1mtlpart11fifo=0 then P1CostChk = True.
// if p1mtlpart12 <> "" and p1mtlpart12fifo=0 then P1CostChk = True.
// if p1mtlpart13 <> "" and p1mtlpart13fifo=0 then P1CostChk = True.
// if p1mtlpart14 <> "" and p1mtlpart14fifo=0 then P1CostChk = True.
