// if p1mtlpart16 <> "" and p1mtlpart16fifo=0 then P1CostChk = True.
// if p1mtlpart17 <> "" and p1mtlpart17fifo=0 then P1CostChk = True.
// if p1mtlpart18 <> "" and p1mtlpart18fifo=0 then P1CostChk = True.
// if p1mtlpart19 <> "" and p1mtlpart19fifo=0 then P1CostChk = True.
// if p1mtlpart20 <> "" and p1mtlpart20fifo=0 then P1CostChk = True.
// if p1mtlseq21 <> 0 then P1CostChk = True.
