                    Inputs.var_fgnum.Value = PCLookUp.DataLookup("CafeBannerBannerType", "fg_num", Inputs.cmb_postselection.Value);
                }

                /* Finds rod FG number from csv */
                if (Inputs.cmb_productselection.Value == "Rod")
                {
                    Inputs.var_fgnum.Value = PCLookUp.DataLookup("CafeBannerBannerType", "fg_num", Inputs.cmb_rodselection.Value);
                }


                /* Finds banner FG number from csv */
