                }


                /*FG description and number String generation*/
                Inputs.var_fgdesc.Value = "";
                Inputs.var_fgvalidity.Value = "";

                Inputs.var_fgdesc.Value = PCLookUp.DataLookup("TENUK_FG_MasterTable", "fg_desc", Inputs.var_fgnum.Value);

                if (Inputs.var_fgnum.Value == "") { Inputs.var_fgvalidity.Value = "** ERROR ** No Finished Goods number found."; }
                if ((Inputs.var_fgdesc.Value == "") && (Inputs.var_fgnum.Value != "")) { Inputs.var_fgvalidity.Value = "** ERROR ** The Finished Goods number has no TENUK_FG_MasterTable record."; }
                if ((Inputs.var_fgdesc.Value != "") && (Inputs.var_fgnum.Value != "")) { Inputs.var_fgvalidity.Value = "OK"; }

                /* Set standard Warranty Code */
                string warranty = "";
                warranty = PCLookUp.DataLookup("TENUK_FG_MasterTable", "std_warranty", Inputs.var_fgnum.Value);
                if (warranty == "") { Inputs.chr_warranty.Value = "1YR"; }
                if (warranty != "") { Inputs.chr_warranty.Value = warranty; }


                /*BOM Part Verification */

                Inputs.var_bomvalidity.Value = "OK";
