                Inputs.var_ver4.Value = false;

                bool available_part = false;

								/* Inputs.var_part1.Value */
                if (Inputs.chk_partverification.Value == true)
                {
                    available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part1.Value);
                }
                if (available_part) {
                    Inputs.var_ver1.Value = true;
                }
                if (Inputs.chk_partverification.Value == false) { Inputs.var_ver1.Value = false; }
                if ((Inputs.chk_partverification.Value == true) && (Inputs.var_part1.Value != "") && (Inputs.var_ver1.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }


								/* Inputs.var_part2.Value */
                if (Inputs.chk_partverification.Value == true)
                {
                    available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part2.Value);
                }
                if (available_part) {
                    Inputs.var_ver2.Value = true;
                }
                if (Inputs.chk_partverification.Value == false) { Inputs.var_ver2.Value = false; }
                if ((Inputs.chk_partverification.Value == true) && (Inputs.var_part2.Value != "") && (Inputs.var_ver2.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }


								/* Inputs.var_part3.Value */
                if (Inputs.chk_partverification.Value == true)
                {
                    available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part3.Value);
                }
                if (available_part) {
                    Inputs.var_ver3.Value = true;
                }
                if (Inputs.chk_partverification.Value == false) { Inputs.var_ver3.Value = false; }
                if ((Inputs.chk_partverification.Value == true) && (Inputs.var_part3.Value != "") && (Inputs.var_ver3.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

								/* Inputs.var_part4.Value */
								if (Inputs.chk_partverification.Value == true)
                {
