                if (Inputs.var_part3.Value != "")
                {
										string Description = "";
                    available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.var_part3.Value, out Description);
                    if (available_part) {
                        BannerDesc = Description;
                    }

                    if ((Inputs.var_part3.Value != "") && (BannerDesc != "")) { Inputs.var_linecomments.Value = BannerDesc; }
                }
                if (Inputs.var_part3.Value == "") { Inputs.var_linecomments.Value = ""; }

                /* Validity Routines */

                /* Checks if FG Number passed to sales line is less than 50 Characters */
                decimal smartcount = 0m;
                smartcount = 0;
                smartcount = Inputs.var_smart2.Value.Length;
                if (smartcount > 50)
                {

										MessageBox.Show("The Finished Goods Number exceeds the Maximum number of Characters allowed in the Sales Line Part Number Field......",
										"Note",
										MessageBoxButtons.OK,
										MessageBoxIcon.Information);

