                }

                /* Calculates Net Weight */

                decimal CompWeight1 = 0m;
                decimal CompWeight2 = 0m;
                decimal CompWeight3 = 0m;
                decimal CompWeight4 = 0m;
								Inputs.var_netweight.Value = 0;
								decimal PartWeight = 0m;

								/* CompWeight1 */
								if (Inputs.var_part1.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part1.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight1 = PartWeight * Inputs.dec_part1qty.Value;
                }

								/* CompWeight2 */
                if (Inputs.var_part2.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part2.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight2 = PartWeight * Inputs.dec_part2qty.Value;
                }

								/* CompWeight3 */
								if (Inputs.var_part3.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part3.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight3 = PartWeight * Inputs.dec_part3qty.Value;
                }

								/* CompWeight4 */
                if (Inputs.var_part4.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part4.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight4 = PartWeight* Inputs.dec_part4qty.Value;
                }

                /* Totals the individual component weights */
                Inputs.var_netweight.Value = CompWeight1 + CompWeight2 + CompWeight3 + CompWeight4;

                /* Used to display Netweight list incase verification is required */
                Inputs.var_weightlookup.Value = "1(" + CompWeight1.ToString() + ")" + " - 2(" + CompWeight2.ToString() + ")" + " - 3(" + CompWeight3.ToString() + ")" + " - 4(" + CompWeight4.ToString() + ")";


                /* ************************************************************************************************************************ */

                /* Returns Commodity Code from CSV */

                string commoditystart = "";

                commoditystart = PCLookUp.DataLookup("TENUK_FG_MasterTable", "commodity_code", Inputs.var_fgnum.Value);

                /* If no commodity code is returned from the CSV file it checks the parts master table */
                string partclass = "";
                partclass = "";
                if (commoditystart == "")
                {
										string PartClass = "";
                    available_part = UDMethods.GetPartClass(Context.CompanyID, Inputs.var_fgnum.Value, out PartClass);
                    if (available_part) {
                        partclass = PartClass;
                    }

                }
                /* As Part Class is used to assign Commodity Code */
                if ((commoditystart == "") && (partclass != ""))
                {

									string CommodityCode = "";
                  bool available_partclass = UDMethods.GetPartCommodityCode(Context.CompanyID, partclass, out CommodityCode);
                  if (available_partclass) {
                      commoditystart = CommodityCode;
                  }

                }

                /* Converts commodity code based on Current Company */

                string commodityconverted = "";
                commodityconverted = "";

                if (commoditystart != "")
                {
                    commodityconverted = PCLookUp.DataLookup("CommodityCode_Conversion_Matrix", Context.CompanyID , commoditystart);
                }

                /* Verifies Commodity code exists in IComCode Table within Epicor */
                string commoditycheck = "";
                commoditycheck = "";
                if (commodityconverted != "")
                {
									  string ICommCodeImportCode = "";
                    bool available_ICommCode = UDMethods.GetICommImportCode(Context.CompanyID, commodityconverted, out ICommCodeImportCode);
                    if (available_ICommCode) {
                        commoditycheck = ICommCodeImportCode;
                    }
                }

                if (commodityconverted != commoditycheck)
                {

										MessageBox.Show("The Configurator has assigned a Commodity code that does not have a corresponding Epicor record, please contact an Epicor Administrator to correct this error.",
										"Error",
										MessageBoxButtons.OK,
										MessageBoxIcon.Exclamation);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }

                if (commodityconverted != "") { Inputs.var_commodity.Value = commodityconverted; }
                if (commodityconverted == "") { Inputs.var_commodity.Value = commoditystart; }


                if (Inputs.var_commodity.Value == "")
                {
										MessageBox.Show("The Configurator has been unable to determine the Commodity Code for the chosen configuration. Please contact the Operations department.",
										"Error",
										MessageBoxButtons.OK,
										MessageBoxIcon.Exclamation);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }
                else if ((Inputs.var_commodity.Value != "") || (Inputs.var_commodity.Value == commoditycheck))
                {
                    PageLeaveFunctions.SetNextInputPage(3);
                }

                /* ********************************************************************************************************************************************************************************** */

							  /* Pack Comment */

							  string Mode = "";
							  int CustID = 0;
							  int OrderNum = 0;
							  int QuoteNum = 0;
							  string CustCountry = "";
							  string CustName = "";
							  string OTSID = "";
							  bool OTS = false;
							  int OTSCountry = 0;
								string currencyCode = "";

							  /* Determines Current Configuration Mode and order/quote specifics */
							  Mode = Context.Entity;
							  CustID = 0; /* Set to Zero when not used for testing*/
							  CustCountry = "";
							  OrderNum = Context.OrderNumber;
							  QuoteNum = Context.QuoteNumber;

								/* ********************************************************************************************************************************************************************************** */
								if (Mode == "Quote")
								{
										/* Retrieve details from Quote Header */
										bool available_QuoteHed = UDMethods.GetQuoteHed(Context.CompanyID, QuoteNum, out CustID, out OTSID, out OTS, out currencyCode);

								    if (available_QuoteHed)
								    {
								        /*Inputs.var_custid.Value = CustID;*/
								        /*Inputs.var_custcurrency.Value = currencyCode;*/
								        /*Inputs.var_custcurrency1.Value = currencyCode;*/
								    }                  

										/* ************************************************************** */

										/* Retrieves customer name using customer ID */
										bool available_Customer = UDMethods.GetCustomer(Context.CompanyID, CustID, out CustName, out CustCountry, out currencyCode);

										/* If a saved shipto address is specified gets country info */
										if (OTSID != "")
										{
										    bool available_ShipTo = UDMethods.GetShipToCountryNum(Context.CompanyID, OTSID, out OTSCountry);
										    bool available_Country = UDMethods.GetCountryDescription(Context.CompanyID, OTSCountry, out CustCountry);
										}

									  Inputs.var_orderdtl.Value = "Mode: " + Mode + "  |  Order/Quote No: " + QuoteNum.ToString() + "   | Cust: " + CustName + "  |   ShipCountry: " + CustCountry;
									  Inputs.var_shipcountry.Value = CustCountry;
								}

								/* ********************************************************************************************************************************************************************************** */
								if (Mode == "Order")
								{
										/* Retrieve details from Order Header */
								      bool available_OrderHed = UDMethods.GetOrderHed(Context.CompanyID, OrderNum, out CustID, out OTSID, out OTS, out currencyCode);

								      if (available_OrderHed)
								      {
								        /*Inputs.var_custid.Value = CustID;
								        Inputs.var_custcurrency.Value = currencyCode;
								        Inputs.var_custcurrency1.Value = currencyCode;*/
								      }

								      /* Retrieves customer name using customer ID */
								      bool available_Customer = UDMethods.GetCustomer(Context.CompanyID, CustID, out CustName, out CustCountry, out currencyCode);

								      /* If a saved shipto address is specified gets country info */
								      if (OTSID != "")
								      {
								          bool available_ShipTo = UDMethods.GetShipToCountryNum(Context.CompanyID, OTSID, out OTSCountry);
													bool available_Country = UDMethods.GetCountryDescription(Context.CompanyID, OTSCountry, out CustCountry);
								      }

										 Inputs.var_orderdtl.Value = "Mode: " + Mode + "  |  Order/Quote No: " + OrderNum.ToString() + "   | Cust: " + CustName + "  |   ShipCountry: " + CustCountry;
										 Inputs.var_shipcountry.Value = CustCountry;
								}

								/* ********************************************************************************************************************************************************************************** */
								if (Mode == "PcStatus")
								{
                    Inputs.var_orderdtl.Value = "Mode: " + Mode;
                    /* Inputs.var_shipcountry.Value = "Albania". */
								}


                string palletlookup = "";

                palletlookup = "";

                /* Finds Pallet lookup from csv based on Shipping country*/
                palletlookup = PCLookUp.DataLookup("CountryPalletList", "pallet_type", Inputs.var_shipcountry.Value);

