// Enter valid C# code and do not forget this method returns a boolean value.
Erp.Tables.Currency currency;

CurrencyCode = "";

currency = (from Currency_Row in Db.Currency
		    where (Currency_Row.Company == Company && Currency_Row.BaseCurr == true)
		    select Currency_Row).FirstOrDefault();

if (currency == null) { return false; }
else {
  CurrencyCode =  currency.CurrencyCode;
  return true;
}

// Enter valid C# code and do not forget this method returns a boolean value.
Erp.Tables.Country country;

Description = "";

country = (from country_Row in Db.Country
		    where (country_Row.Company == Company && country_Row.CountryNum == CountryNum)
		    select country_Row).FirstOrDefault();

if (country == null) { return false; }
else {
  Description = country.Description;  
  return true;
}

Name = "";
Country = "";
currencycode = "";

var customer = (from customer_Row in Db.Customer
		    where (customer_Row.Company == Company && customer_Row.CustNum == CustNum)
		    select new {customer_Row.Name, customer_Row.Country, customer_Row.CurrencyCode}).FirstOrDefault();

if (customer == null) { return false; }
else {
  Name = customer.Name;  
  Country = customer.Country;
  currencycode = customer.CurrencyCode;
  return true;
}

