		    where (iCommCode_Row.Company == Company && iCommCode_Row.CommodityCode == CommodityCode)
		    select new {iCommCode_Row.ImportCode}).FirstOrDefault();

if (iCommCode == null) { return false; }
else {
  ICommCodeImportCode = iCommCode.ImportCode;
  return true;
}

