if (Inputs.chk_bespoke.Value == true) {
  Inputs.cmb_partentry.Value = "";
  Inputs.chr_partdesc.Value = "";
  Inputs.chk_selectcommodity.Value = true;
  Inputs.RADIOSET3.Value = "2";
}








