if ((Inputs.cmb_partentry.Value!="") && ( Inputs.chk_manualdesc.Value == false)) {
  string Description = "";
  bool available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.cmb_partentry.Value, out Description);

  if (available_part) {
    Inputs.chr_partdesc.Value = Description;
  }

  available_part = false; // UDMethods.GetpartFunction(Context.CompanyID, Inputs.cmb_partentry.Value, out partitem);




  if (available_part) {
		/*Inputs.var_suom.Value = part.SalesUM;*/
  }

  if (Inputs.var_suom.Value == "M") {

		MessageBox.Show("This configurator has been restricted to only allow selection of Parts with a Unit of Measure of Each. The Part you have selected has a UOM of Metres. Please cancel this configuration and choose the Configurator trigger part of ITMM in the Sales Order Line then configure as normal.",
		"Note",
		MessageBoxButtons.OK,
		MessageBoxIcon.Warning);

  }

  if (Inputs.var_suom.Value == "G") {


		MessageBox.Show("This configurator has been restricted to only allow selection of Parts with a Unit of Measure of Each. The Part you have selected has a UOM of Grams. Please cancel this configuration and choose the Configurator trigger part of ITMG in the Sales Order Line then configure as normal.",
		"Note",
		MessageBoxButtons.OK,
		MessageBoxIcon.Warning);


  }

  if (Inputs.var_suom.Value == "KG") {


		MessageBox.Show("This configurator has been restricted to only allow selection of Parts with a Unit of Measure of Each. The Part you have selected has a UOM of Kilos. Please cancel this configuration and choose the Configurator trigger part of ITMK in the Sales Order Line then configure as normal.",
		"Note",
		MessageBoxButtons.OK,
		MessageBoxIcon.Warning);

  }

  /* ************************************************************************************************************************** */

  /* Initial Commodity Code determination */

  /* Returns Commodity Code from CSV */

  string commoditystart = "";

  commoditystart = PCLookUp.DataLookup("TENUK_FG_MasterTable", "commodity_code", Inputs.cmb_partentry.Value);

  /* If no commodity code is returned from the CSV file it checks the parts master table */
  string partclass = "";
  partclass="";
  if (commoditystart=="") {
    string PartClass = "";
    available_part = UDMethods.GetPartClass(Context.CompanyID,Inputs.cmb_partentry.Value, out PartClass);

    if (available_part) {
      partclass = PartClass;
    }
  }
  /* As Part Class is used to assign Commodity Code */
  if ((commoditystart=="") && ( partclass!="")) {
    string CommodityCode = "";
    bool available_partclass = UDMethods.GetPartCommodityCode(Context.CompanyID,partclass, out CommodityCode);
    if (available_part) {
      commoditystart = CommodityCode;
    }
  }

  /* Converts commodity code based on Current Company */

  string commodityconverted = "";
  commodityconverted = "";

  if (commoditystart!="") {
    commodityconverted = PCLookUp.DataLookup("", Context.CompanyID, commoditystart);
  }

  /* Verifies Commodity code exists in IComCode Table within Epicor */
  string commoditycheck = "";

  if (commodityconverted!="") {
    string ICommCodeImportCode = "";
    bool available_ICommCode = UDMethods.GetICommImportCode(Context.CompanyID,commodityconverted, out ICommCodeImportCode);

    if (available_ICommCode) {
      commoditycheck = ICommCodeImportCode;
    }
  }


  if (commodityconverted!=commoditycheck) {

		MessageBox.Show("The Configurator has assigned a Commodity code that does not have a corresponding Epicor record, please contact an Epicor Administrator to correct this error.",
		"Note",
		MessageBoxButtons.OK,
		MessageBoxIcon.Warning);

  }

  /* Set the commodity code on Page1 */
if (commodityconverted !="") {Inputs.chr_commodity.Value = commodityconverted;}
if (commodityconverted == "") {Inputs.chr_commodity.Value = commoditystart;}

}




















