  weblengthcutactive = PCLookUp.DataLookup("WebbingLengthConversionFactors", "SWEB_active", Inputs.cmb_webbinglength.Value);

if (weblengthcutactive == "TRUE") {Inputs.var_weblengthcutactive.Value = "TRUE";}
if (weblengthcutactive != "TRUE") {Inputs.var_weblengthcutactive.Value = "FALSE";}

  /* Set the Web Format parameter */

if (Inputs.var_weblengthcutactive.Value == "TRUE") {Inputs.cmb_webformat.Value = "CUT";}
if (Inputs.var_weblengthcutactive.Value == "FALSE") {Inputs.cmb_webformat.Value = "ROLL";}

}

