PcPageExpr.Expression:
            string coretype = "";
            string prodgroup = "";
            string weblength1 = "";
            string webcol = "";
            string tapeend = "";
            string qmaticlookup = "";
            string casscommentlookup = "";
            string weblength = "";

						string customerNameGlobal = "";

						bool available_part = false;


            if ((Inputs.cmb_coresolution.Value == "") || (Inputs.cmb_sumsalescat.Value == "") || (Inputs.cmb_sector.Value == ""))
            {

								MessageBox.Show("You have not entered all mandatory options...",
								"Note",
								MessageBoxButtons.OK,
								MessageBoxIcon.Information);

						    PageLeaveFunctions.SetNextInputPage(1);
								Args.Cancel = true;


            }
            else if ((Inputs.cmb_coresolution.Value != "") && (Inputs.cmb_sector.Value != ""))
            {

                /*FG description and number String generation*/
                Inputs.var_fgdesc.Value = "";
                Inputs.var_fgvalidity.Value = "";

                /* Determines if an alternate description string is required */
                string altdescrqd = "";
                altdescrqd = "";
                altdescrqd = PCLookUp.DataLookup("CassetteRange", "fg_desc_type", Inputs.cmb_cassetteselection.Value);

                if (altdescrqd == "")
                {
                    Inputs.var_fgdesc.Value = PCLookUp.DataLookup("TENUK_FG_MasterTable", "fg_desc", Inputs.var_fgnum.Value);
                }

                if (altdescrqd != "")
                {
                    Inputs.var_fgdesc.Value = PCLookUp.DataLookup("TENUK_FG_MasterTable", altdescrqd, Inputs.var_fgnum.Value);
                }


                if (Inputs.var_fgnum.Value == "") { Inputs.var_fgvalidity.Value = "** ERROR ** No Finished Goods number found."; }
                if ((Inputs.var_fgdesc.Value == "") && (Inputs.var_fgnum.Value != "")) { Inputs.var_fgvalidity.Value = "** ERROR ** The Finished Goods number has no TENUK_FG_MasterTable record."; }
                if ((Inputs.var_fgdesc.Value != "") && (Inputs.var_fgnum.Value != "")) { Inputs.var_fgvalidity.Value = "OK"; }

                /* Set standard Warranty Code */
                string warranty = "";
                warranty = PCLookUp.DataLookup("TENUK_FG_MasterTable", "std_warranty", Inputs.var_fgnum.Value);
                if (warranty == "") { Inputs.chr_warranty.Value = "1YR"; }
                if (warranty != "") { Inputs.chr_warranty.Value = warranty; }


                /*BOM Part Verification */

                Inputs.var_bomvalidity.Value = "OK";
                Inputs.chk_ver1.Value = false;
                Inputs.chk_ver2.Value = false;
                Inputs.chk_ver3.Value = false;
                Inputs.chk_ver4.Value = false;
                Inputs.chk_ver5.Value = false;

                if (Inputs.chk_partverification.Value == false) { Inputs.var_bomvalidity.Value = "** WARNING ** Bom verification is switched OFF."; }


								/* Inputs.var_cassettepartnum.Value */
                if (Inputs.chk_partverification.Value == true)
                {
                    available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_cassettepartnum.Value);
                }
                if (available_part) {
                    Inputs.chk_ver1.Value = true;
                }
                if (Inputs.chk_partverification.Value == false) { Inputs.chk_ver1.Value = false; }
                if ((Inputs.chk_partverification.Value == true) && (Inputs.var_cassettepartnum.Value != "") && (Inputs.chk_ver1.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

								/* Inputs.var_wedgepartnum.Value */
                if (Inputs.chk_partverification.Value == true)
                {
                    available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_wedgepartnum.Value);
                }
                if (available_part) {
                    Inputs.chk_ver2.Value = true;
                }
                if (Inputs.chk_partverification.Value == false) { Inputs.chk_ver2.Value = false; }
                if ((Inputs.chk_partverification.Value == true) && (Inputs.var_wedgepartnum.Value != "") && (Inputs.chk_ver2.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

								/* Inputs.var_webpartnum.Value */
                if (Inputs.chk_partverification.Value == true)
                {
                    available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_webpartnum.Value);
                }
                if (available_part) {
                    Inputs.chk_ver3.Value = true;
                }
                if (Inputs.chk_partverification.Value == false) { Inputs.chk_ver3.Value = false; }
                if ((Inputs.chk_partverification.Value == true) && (Inputs.var_webpartnum.Value != "") && (Inputs.chk_ver3.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

								/* Inputs.var_tapeendpartnum.Value */
                if (Inputs.chk_partverification.Value == true)
                {
                    available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_tapeendpartnum.Value);
                }
                if (available_part) {
                    Inputs.chk_ver4.Value = true;
                }
                if (Inputs.chk_partverification.Value == false) { Inputs.chk_ver4.Value = false; }
                if ((Inputs.chk_partverification.Value == true) && (Inputs.var_tapeendpartnum.Value != "") && (Inputs.chk_ver4.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }


								/* Inputs.var_topcappartnum.Value */
                if (Inputs.chk_partverification.Value == true)
                {
                    available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_topcappartnum.Value);
                }
                if (available_part) {
                    Inputs.chk_ver5.Value = true;
                }
                if (Inputs.chk_partverification.Value == false) { Inputs.chk_ver5.Value = false; }
                if ((Inputs.chk_partverification.Value == true) && (Inputs.var_topcappartnum.Value != "") && (Inputs.chk_ver5.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

                /*Smart Code string generation */

                if (Inputs.cmb_coresolution.Value == "Core") { coretype = "COR"; }
                if (Inputs.cmb_coresolution.Value == "Solution") { coretype = "SOL"; }

                if (Inputs.cmb_productgroup.Value == "Tensabarrier Cassette Range") { prodgroup = "TBR"; }
                if (Inputs.cmb_productgroup.Value == "Tensamedia") { prodgroup = "TME"; }
                if (Inputs.cmb_productgroup.Value == "Tensabright") { prodgroup = "BRI"; }
                if (Inputs.cmb_productgroup.Value == "Night View") { prodgroup = "NV"; }
                if (Inputs.cmb_productgroup.Value == "Night View Tensamedia") { prodgroup = "NVTM"; }

                /* Not Night View Webbing */
                if ((Inputs.chr_webbingtype.Value != "") && (Inputs.chk_nvweb.Value == false)) { weblength1 = Inputs.chr_webbingtype.Value; }
                if ((Inputs.chr_webbingtype.Value == "") && (Inputs.chk_nvweb.Value == false)) { weblength1 = "XXX"; }

                /* Night View Webbing */
                if ((Inputs.chr_webbingtype.Value == "STD") && (Inputs.chk_nvweb.Value == true)) { weblength1 = "NVS"; }
                if ((Inputs.chr_webbingtype.Value == "MAX") && (Inputs.chk_nvweb.Value == true)) { weblength1 = "NVM"; }
                if ((Inputs.chr_webbingtype.Value == "WDE") && (Inputs.chk_nvweb.Value == true)) { weblength1 = "NVW"; }
                if ((Inputs.chr_webbingtype.Value == "RCV") && (Inputs.chk_nvweb.Value == true)) { weblength1 = "XXX"; }
                if ((Inputs.chr_webbingtype.Value == "") && (Inputs.chk_nvweb.Value == true)) { weblength1 = "XXX"; }


                if (Inputs.var_smartcodetapeend.Value != "") { tapeend = Inputs.var_smartcodetapeend.Value; }
                if (Inputs.var_smartcodetapeend.Value == "") { tapeend = "XXX"; }


                if (Inputs.cmb_webbingfinish.Value != "") { webcol = Inputs.cmb_webbingfinish.Value.Entry(1, "-"); }
                if (Inputs.cmb_webbingfinish.Value == "") { webcol = "XXX"; }

                string sumsalescat = "";

                if (Inputs.cmb_sumsalescat.Value == "Core") { sumsalescat = "COR"; }
                if (Inputs.cmb_sumsalescat.Value == "Solution") { sumsalescat = "SOL"; }

                Inputs.var_smartstring.Value = sumsalescat + "|" + coretype + "-" + prodgroup + "|" + Inputs.var_smartfg.Value + "|" + "XXX" + "|" + "XXX" + "|" + "XXX" + "|" + weblength1 + "|" + webcol + "|" + tapeend + "|" + "XXX" + "|" + "XXX" + "|" + "XXX" + "|" + Inputs.var_sectorcode.Value;

                Inputs.var_smart1.Value = coretype + "-" + prodgroup;
                Inputs.var_smart2.Value = Inputs.var_smartfg.Value + "+" + "XXX" + "-" + "XXX" + "-" + "XXX" + "-" + weblength1 + "-" + webcol + "-" + tapeend + "-" + "XXX" + "-" + "XXX" + "-" + "XXX";
                Inputs.var_smart3.Value = Inputs.var_sectorcode.Value;


                /* Finds Qmatic part/desc details from csv */
                if ((Inputs.chk_customwebbing.Value == false) && (Inputs.cmb_cassrange.Value == "Qmatic")) { qmaticlookup = Inputs.cmb_cassetteselection.Value + "-" + Inputs.var_webpartnum.Value; }
                if ((Inputs.chk_customwebbing.Value == true) && (Inputs.cmb_cassrange.Value == "Qmatic")) { qmaticlookup = Inputs.cmb_cassetteselection.Value + "-" + Inputs.var_qmspecialweb.Value; }

                Inputs.var_qmaticpartnum.Value = PCLookUp.DataLookup("QmaticCassetteFGLookup", "qmatic_partnum", qmaticlookup);
                Inputs.var_qmaticdesc.Value = PCLookUp.DataLookup("QmaticCassetteFGLookup", "qmatic_desc", qmaticlookup);

                Inputs.var_qmaticfgnum.Value = Inputs.var_qmaticpartnum.Value;

                if ((Inputs.var_bomvalidity.Value == "** ERROR ** A component in the BOM has no Epicor Part Master Record.") && (Inputs.chk_erroron.Value == true))
                {

										MessageBox.Show("A Component in the BOM does not exist in the Parts Master table, or the record has been made inactive. Please contact the Operations department to resolve this issue.",
										"Error",
										MessageBoxButtons.OK,
										MessageBoxIcon.Exclamation);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }

                /* Validity Routines */

                /* Checks if FG Number passed to sales line is less than 50 Characters */
                decimal smartcount = 0m;
                smartcount = 0;
                smartcount = Inputs.var_smart2.Value.Length;
                if (smartcount > 50)
                {

										MessageBox.Show("The Finished Goods Number exceeds the Maximum number of Characters allowed in the Sales Line Part Number Field......",
										"Note",
										MessageBoxButtons.OK,
										MessageBoxIcon.Information);

										PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }

                /* Checks if fg description has been passed */
                string fgdesccheck = "";
                string fgdesc2 = "";
                fgdesccheck = Inputs.var_fgdesc.Value;
                fgdesc2 = "";
                if (fgdesccheck == "")
                {
										string Description = "";
                    available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.var_fgnum.Value, out Description);
                    if (available_part) {
                        fgdesc2 = Description;
                    }

                    if (fgdesc2 != "")
                    {
                        Inputs.var_fgdesc.Value = fgdesc2;
                        Inputs.var_fgvalidity.Value = "OK";
                    }
                    if ((fgdesc2 == "") && (Inputs.var_qmaticfgnum.Value == ""))
                    {

												MessageBox.Show("No Description has been returned for the Finished Goods Number.........",
												"Note",
												MessageBoxButtons.OK,
												MessageBoxIcon.Information);

										    		PageLeaveFunctions.SetNextInputPage(1);
												Args.Cancel = true;


                    }
                }

                /* Auto Sales Comment Line */

                if (Inputs.cmb_webbinglength.Value == "Custom")
                {
                    weblength = Inputs.dec_customweblength.Value.ToString() ;
                }

                if (Inputs.cmb_webbinglength.Value != "Custom")
                {
                    weblength = Inputs.cmb_webbinglength.Value;
                }


                casscommentlookup = PCLookUp.DataLookup("cassetterange", "comment_string", Inputs.cmb_cassetteselection.Value);


                /* Removes the Cut and Stitch reference from the webbing part description */

                string replacecutref = "";
                string revisedwebdesc = "";

                /*replacecutref = REPLACE(Inputs.var_webdesc.Value, "cut", "+");
                revisedwebdesc = TRIM(LC(replacecutref.Entry(0, "+")));*/

								replacecutref = Inputs.var_webdesc.Value.Replace("cut", "+");
								revisedwebdesc = replacecutref.ToLower().Trim('+');


                if ((Inputs.cmb_cassrange.Value != "Receivers") && (Inputs.chk_nowebbing.Value == false))
                {
                    Inputs.var_linecomment.Value = casscommentlookup + ". " + Inputs.cmb_tapeendselection.Value + " tape end with " + weblength + " metres of " + revisedwebdesc + ".";
                    Inputs.var_jobcomments.Value = casscommentlookup + ". " + Inputs.cmb_tapeendselection.Value + " tape end with " + weblength + " metres of " + Inputs.cmb_webbingfinish.Value + " webbing.";
                }

                if (Inputs.cmb_cassrange.Value == "Receivers")
                {
                    Inputs.var_linecomment.Value = casscommentlookup;
                    Inputs.var_jobcomments.Value = Inputs.var_linecomment.Value;
                }

                if (Inputs.chk_nowebbing.Value == true)
                {
                    Inputs.var_linecomment.Value = casscommentlookup + " with no webbing";
                    Inputs.var_jobcomments.Value = Inputs.var_linecomment.Value;
                }

                if (Inputs.var_qmaticdesc.Value != "")
                {
                    Inputs.var_fgdesc.Value = Inputs.var_qmaticdesc.Value;
                }

                if ((Inputs.var_qmaticdesc.Value == "") && (Inputs.var_qmaticfgnum.Value != ""))
                {

										MessageBox.Show("No Description has been returned for the Qmatic Finished Goods Number. Please contact the Operations department to correct this error...",
										"Error",
										MessageBoxButtons.OK,
										MessageBoxIcon.Exclamation);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }


                /* Appears on packing label as note string */

                string labelcasstype = "";
                string labelwebbing = "";
                string labelweblength = "";

                if (Inputs.chr_webbingtype.Value != "") { labelcasstype = Inputs.chr_webbingtype.Value + " cass, "; }
                if (Inputs.cmb_webbingfinish.Value != "") { labelwebbing = Inputs.var_webdesc.Value; }
                if (weblength != "") { labelweblength = weblength + "m "; }

                Inputs.var_labeldesc.Value = labelweblength + labelcasstype + labelwebbing;

                /* Calculates Net Weight */

                decimal CompWeight1 = 0m;
                decimal CompWeight2 = 0m;
                decimal CompWeight3 = 0m;
                decimal CompWeight4 = 0m;
                decimal CompWeight5 = 0m;
                decimal CompWeight6 = 0m;
								decimal PartWeight = 0m;


								/* CompWeight1 */
								if (Inputs.var_cassettepartnum.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_cassettepartnum.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight1 = PartWeight * Inputs.var_cassetteqty.Value;
                }


								/* CompWeight2 */
                if (Inputs.var_wedgepartnum.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_wedgepartnum.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight2 = PartWeight * Inputs.var_wedgeqty.Value;
                }

								/* CompWeight3 */
                if (Inputs.var_webpartnum.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_webpartnum.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight3 = PartWeight * Inputs.var_webqty.Value;
                }

								/* CompWeight4 */
                if (Inputs.var_tapeendpartnum.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_tapeendpartnum.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight4 = PartWeight * Inputs.var_tapeendqty.Value;
                }

								/* CompWeight5 */
                if (Inputs.var_topcappartnum.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_topcappartnum.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight5 = PartWeight * Inputs.var_topcapqty.Value;
                }

                /* Totals the individual component weights */
                Inputs.var_netweight.Value = CompWeight1 + CompWeight2 + CompWeight3 + CompWeight4 + CompWeight5;

                /* Used to display Netweight list incase verification is required */
                Inputs.var_weightlookup.Value = "1(" + CompWeight1.ToString() + ")" + " - 2(" + CompWeight2.ToString() + ")" + " - 3(" + CompWeight3.ToString() + ")" + " - 4(" + CompWeight4.ToString() + ")" + " - 5(" + CompWeight5.ToString() + ")";

                /* ************************************************************************************************************************ */

                /* Returns Commodity Code from CSV */

                string commoditystart = "";

                commoditystart = PCLookUp.DataLookup("TENUK_FG_MasterTable.", "commodity_code", Inputs.var_fgnum.Value);

                /* If no commodity code is returned from the CSV file it checks the parts master table */
                string partclass = "";

                if (commoditystart == "")
                {
										string PartClass = "";
                    available_part = UDMethods.GetPartClass(Context.CompanyID, Inputs.var_fgnum.Value, out PartClass);
                    if (available_part) {
                        partclass = PartClass;
                    }

                }
                /* As Part Class is used to assign Commodity Code */
                if ((commoditystart == "") && (partclass != ""))
                {
									string CommodityCode = "";
                  bool available_partclass = UDMethods.GetPartCommodityCode(Context.CompanyID, partclass, out CommodityCode);
                  if (available_partclass) {
                      commoditystart = CommodityCode;
                  }

                }

                /* Converts commodity code based on Current Company */

                string commodityconverted = "";
                commodityconverted = "";

                if (commoditystart != "")
                {
                    commodityconverted = PCLookUp.DataLookup("", Context.CompanyID, commoditystart);
                }

                /* Verifies Commodity code exists in IComCode Table within Epicor */
                string commoditycheck = "";
                commoditycheck = "";
                if (commodityconverted != "")
                {

									  string ICommCodeImportCode = "";
                    bool available_ICommCode = UDMethods.GetICommImportCode(Context.CompanyID, commodityconverted, out ICommCodeImportCode);
                    if (available_ICommCode) {
                        commoditycheck = ICommCodeImportCode;
                    }

                }

                if (commodityconverted != commoditycheck)
                {
										MessageBox.Show("The Configurator has assigned a Commodity code that does not have a corresponding Epicor record, please contact an Epicor Administrator to correct this error.",
										"Error",
										MessageBoxButtons.OK,
										MessageBoxIcon.Exclamation);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }

                if (commodityconverted != "") { Inputs.var_commodity.Value = commodityconverted; }
                if (commodityconverted == "") { Inputs.var_commodity.Value = commoditystart; }


                if (Inputs.var_commodity.Value == "")
                {

										MessageBox.Show("The Configurator has been unable to determine the Commodity Code for the chosen configuration. Please contact the Operations department.",
										"Error",
										MessageBoxButtons.OK,
										MessageBoxIcon.Exclamation);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }
                else if ((Inputs.var_commodity.Value != "") || (Inputs.var_commodity.Value == commoditycheck))
                {
                    PageLeaveFunctions.SetNextInputPage(4);
                }

							  /* Pack Comment */

							  string Mode = "";
							  int CustID = 0;
							  int OrderNum = 0;
							  int QuoteNum = 0;
							  string CustCountry = "";
							  string CustName = "";
							  string OTSID = "";
							  bool OTS = false;
							  int OTSCountry = 0;
								string currencyCode = "";

							  /* Determines Current Configuration Mode and order/quote specifics */
							  Mode = Context.Entity;
							  CustID = 0; /* Set to Zero when not used for testing*/
							  CustCountry = "";
							  OrderNum = Context.OrderNumber;
							  QuoteNum = Context.QuoteNumber;


								/* ********************************************************************************************************************************************************************************** */
								if (Mode == "Quote")
								{
										/* Retrieve details from Quote Header */
										bool available_QuoteHed = UDMethods.GetQuoteHed(Context.CompanyID, QuoteNum, out CustID, out OTSID, out OTS, out currencyCode);

								    if (available_QuoteHed)
								    {
								        /*Inputs.var_custid.Value = CustID;*/
								        /*Inputs.var_custcurrency.Value = currencyCode;*/
								        /*Inputs.var_custcurrency1.Value = currencyCode;*/
								    }                  

										/* ************************************************************** */

										/* Retrieves customer name using customer ID */
										bool available_Customer = UDMethods.GetCustomer(Context.CompanyID, CustID, out CustName, out CustCountry, out currencyCode);

										/* If a saved shipto address is specified gets country info */
										if (OTSID != "")
										{
										    bool available_ShipTo = UDMethods.GetShipToCountryNum(Context.CompanyID, OTSID, out OTSCountry);
										    bool available_Country = UDMethods.GetCountryDescription(Context.CompanyID, OTSCountry, out CustCountry);
										}

									  Inputs.var_orderdtl.Value = "Mode: " + Mode + "  |  Order/Quote No: " + QuoteNum.ToString() + "   | Cust: " + CustName + "  |   ShipCountry: " + CustCountry;
									  Inputs.var_shipcountry.Value = CustCountry;
										customerNameGlobal = CustName;
								}


								/* ********************************************************************************************************************************************************************************** */
								if (Mode == "Order")
								{
									/* Retrieve details from Order Header */
							      bool available_OrderHed = UDMethods.GetOrderHed(Context.CompanyID, OrderNum, out CustID, out OTSID, out OTS, out currencyCode);

							      if (available_OrderHed)
							      {
							        /*Inputs.var_custid.Value = CustID;
							        Inputs.var_custcurrency.Value = currencyCode;
							        Inputs.var_custcurrency1.Value = currencyCode;*/
							      }

							      /* Retrieves customer name using customer ID */
							      bool available_Customer = UDMethods.GetCustomer(Context.CompanyID, CustID, out CustName, out CustCountry, out currencyCode);

							      /* If a saved shipto address is specified gets country info */
							      if (OTSID != "")
							      {
							          bool available_ShipTo = UDMethods.GetShipToCountryNum(Context.CompanyID, OTSID, out OTSCountry);
												bool available_Country = UDMethods.GetCountryDescription(Context.CompanyID, OTSCountry, out CustCountry);
							      }

									 Inputs.var_orderdtl.Value = "Mode: " + Mode + "  |  Order/Quote No: " + OrderNum.ToString() + "   | Cust: " + CustName + "  |   ShipCountry: " + CustCountry;
									 Inputs.var_shipcountry.Value = CustCountry;
									 customerNameGlobal = CustName;
								}


								/* ********************************************************************************************************************************************************************************** */
								if (Mode == "PcStatus")
								{
                    Inputs.var_orderdtl.Value = "Mode: " + Mode;
                    /* Inputs.var_shipcountry.Value = "Albania". */

                    Inputs.var_custid.Value = 11131;
								}

                /* ********************************************************************************************************************************************************************************** */

                string palletlookup = "";

                palletlookup = "";

                /* Finds Pallet lookup from csv based on Shipping country*/
                palletlookup = PCLookUp.DataLookup("CountryPalletList", "pallet_type", Inputs.var_shipcountry.Value);

                if (palletlookup != "")
                {
                    Inputs.var_palletlookup.Value = palletlookup + "_Lookup";
                    Inputs.var_packcomment.Value = PCLookUp.DataLookup("CountryPalletList", "default_message", Inputs.var_shipcountry.Value);
                }

                if (palletlookup == "") { Inputs.var_palletlookup.Value = "All_Lookup"; }


						}

            /* ********************************************************************************************************************************************************************************** */

            /* Verifies if the customer has unique customer part numbers and checks that a number has been returned based on the configuration */

            string custpartverification = "";

						custpartverification = PCLookUp.DataLookup("Customer_PartNumber_Verification", "Part_Verification_On", customerNameGlobal);


            if ((custpartverification == "True") && (Inputs.var_qmaticfgnum.Value == ""))
            {
								MessageBox.Show("The Configurator has been unable to determine the Customer specific finished goods number for the chosen configuration. Verification is currently set as mandatory for this customer. Please verify the chosen options and correct if required. If the options chosen are correct please notify the Operations department who will add a specific customer reference for the chosen configuration.",
								"Note",
								MessageBoxButtons.OK,
								MessageBoxIcon.Information);

						    PageLeaveFunctions.SetNextInputPage(1);
								Args.Cancel = true;

            }


            /* ********************************************************************************************************************************************************************************** */

            /* Sets conditions if the Order/Quote being keyed is for RS Components which requires selection of an RS Label in the BOM */

            if (Inputs.var_custid.Value == 1113)
            {
                Inputs.var_rslabelpart.Invisible = false;
                Inputs.var_rslabelqty.Invisible = false;
                Inputs.chk_rslabelnone.Invisible = false;
                Inputs.TEXT15.Invisible = false;
                PageLeaveFunctions.SetNextInputPage(3);
            }

            if (Inputs.var_custid.Value != 1113)
            {
                Inputs.var_rslabelpart.Invisible = true;
                Inputs.var_rslabelqty.Invisible = true;
                Inputs.chk_rslabelnone.Invisible = true;
                Inputs.TEXT15.Invisible = true;
                PageLeaveFunctions.SetNextInputPage(4);
            }

            /* ********************************************************************************************************************************************************************************** */

            /* Ensures a webbing has been selected if using the web search feature */

            if ((Inputs.chk_websearchon.Value == true) && (Inputs.cmb_webbingfinish.Value == ""))
            {

								MessageBox.Show("You are using the Web. Search feature and have tried to leave the page without selecting an actual webbing finish. Please correct this before proceeding. ",
								"Note",
								MessageBoxButtons.OK,
								MessageBoxIcon.Information);

								PageLeaveFunctions.SetNextInputPage(1);
								Args.Cancel = true;


            }

