/* Verifies that the value entered does not exceed the maximum length of extrusion avaliable */
string custombeamlengthmessage = "";
decimal maxpurchaselength = 0m;


maxpurchaselength = UDMethods.ConvertToDecimal(PCLookUp.DataLookup("TensaguideBeamTypes", "max_purchase_length", Inputs.cmb_beamtype.Value));


custombeamlengthmessage = "The value entered cannot exceed (" + maxpurchaselength.ToString() + "mm) which is the maximum length of " + Inputs.cmb_beamtype.Value + " currently purchased.";

if (Inputs.dec_custombeamlength.Value > maxpurchaselength) {

		MessageBox.Show(custombeamlengthmessage,
		"Note",
		MessageBoxButtons.OK,
		MessageBoxIcon.Information);
}

if ((Inputs.dec_custombeamlength.Value!=0 ) && ( Inputs.dec_custombeamlength.Value < 500)) {

		MessageBox.Show("The value entered appears too small to be valid please rekey (note the height is expressed in mm)",
		"Note",
		MessageBoxButtons.OK,
		MessageBoxIcon.Information);

}











// /* Verifies that the value entered does not exceed the maximum length of extrusion avaliable */
// Define Variable custombeamlengthmessage As Char No-Undo.
// Define Variable maxpurchaselength As Decimal No-Undo.
// 
// 
// run ud\test\FileLookUp.p ("\\EPICORAPP1\Epicor905\Server\ud\" + Inputs.var_csvfolder.Value + "\TensaguideBeamConfigurator\TensaguideBeamTypes.csv","max_purchase_length",Inputs.cmb_beamtype.Value, output maxpurchaselength  ).
