/* Determines the Folder with the CSV Files */

//Inputs.var_csvfolder.Value = PCLookUp.DataLookup("GroupCSVFolderRef", "Folder_Ref", GetCurrentCompany());










// /* Determines the Folder with the CSV Files */
// 
// run ud\test\FileLookUp.p ("\\EPICORAPP1\Epicor905\Server\ud\GroupCSVReferences\GroupCSVFolderRef.csv","Folder_Ref",GetCurrentCompany(), output Inputs.var_csvfolder.Value ).
//

PcPageExpr.Expression:
			bool available_part = false;			

			if ((Inputs.cmb_sector.Value == "") || (Inputs.cmb_sumsalescat.Value == "") || (Inputs.cmb_productgroup.Value == ""))
			{

						MessageBox.Show("You have not answered all the mandatory questions.",
						"Note",
						MessageBoxButtons.OK,
						MessageBoxIcon.Information);

				    PageLeaveFunctions.SetNextInputPage(1);
						Args.Cancel = true;
			}
			else if ((Inputs.cmb_coresolution.Value != "") && (Inputs.cmb_sector.Value != "") && (Inputs.cmb_productgroup.Value != ""))
			{

								/* Displays a popup message if a custom beam length is selected but with no custom length entered */

                if ((Inputs.cmb_beamlength.Value == "Custom") && (Inputs.dec_custombeamlength.Value == 0))
                {
										MessageBox.Show("You have selected a Custom Beam Length but not entered a Custom Length Value....",
										"Note",
										MessageBoxButtons.OK,
										MessageBoxIcon.Information);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;
                }

                /* BOM Determination */

                /* Beam Part Number */
                if ((Inputs.var_beampartprefix.Value != "") && (Inputs.var_beampartsuffix.Value != "")) { Inputs.var_part1.Value = Inputs.var_beampartprefix.Value + "-" + Inputs.var_beampartsuffix.Value; }
                if ((Inputs.cmb_beamlength.Value != "Custom") && (Inputs.var_part1.Value != "")) { Inputs.var_partqty1.Value = 1; }


                /* Standard Beam */
                if ((Inputs.cmb_beamlength.Value == "Custom") && (Inputs.cmb_beamtype.Value == "Standard Beam Extrusion") && (Inputs.var_part1.Value != "")) { Inputs.var_partqty1.Value = (Inputs.dec_custombeamlength.Value - 120) / 1000; }
                /* Oval Beam */
                if ((Inputs.cmb_beamlength.Value == "Custom") && (Inputs.cmb_beamtype.Value == "Oval Beam") && (Inputs.var_part1.Value != "")) { Inputs.var_partqty1.Value = (Inputs.dec_custombeamlength.Value - 132.5m) / 1000; }
                if ((Inputs.var_beampartprefix.Value == "") || (Inputs.var_beampartsuffix.Value == "")) { Inputs.var_part1.Value = ""; }
                if (Inputs.var_part1.Value == "") { Inputs.var_partqty1.Value = 0; }

                /* Beam End Part Number */
                if (Inputs.var_beamendpartnum.Value != "") { Inputs.var_part2.Value = Inputs.var_beamendpartnum.Value; }
                if (Inputs.var_beamendpartnum.Value == "") { Inputs.var_part2.Value = ""; }
                if (Inputs.var_part2.Value != "") { Inputs.var_partqty2.Value = 2; }
                if (Inputs.var_part2.Value == "") { Inputs.var_partqty2.Value = 0; }

                /* Beam End Assembly Part Number (Oval Beam) */
                if (Inputs.var_beamendassypartnum.Value != "") { Inputs.var_part3.Value = Inputs.var_beamendassypartnum.Value; }
                if (Inputs.var_beamendassypartnum.Value == "") { Inputs.var_part3.Value = ""; }
                if (Inputs.var_part3.Value != "") { Inputs.var_partqty3.Value = 1; }
                if (Inputs.var_part3.Value == "") { Inputs.var_partqty3.Value = 0; }

                /* Beam End Screw Part Number (Not applicable for Oval Beam) */
                if (Inputs.var_part2.Value != "") { Inputs.var_part4.Value = "04733-33S"; }
                if (Inputs.var_part2.Value == "") { Inputs.var_part4.Value = ""; }
                if (Inputs.var_part4.Value != "") { Inputs.var_partqty4.Value = 2; }
                if (Inputs.var_part4.Value == "") { Inputs.var_partqty4.Value = 0; }

                /* Beam Trim Part Number */
                if (Inputs.chk_includetrim.Value == true) { Inputs.var_part5.Value = Inputs.var_trimpartprefix.Value + "-" + Inputs.var_beampartsuffix.Value; }
                if (Inputs.chk_includetrim.Value == false) { Inputs.var_part5.Value = ""; }
                if (Inputs.var_part5.Value != "") { Inputs.var_partqty5.Value = Inputs.var_partqty1.Value; }
                if (Inputs.var_part5.Value == "") { Inputs.var_partqty5.Value = 0; }

                /* Gasket Part Number */
                decimal gasketcalc = 0m;

                if (Inputs.chk_glazingextrusion.Value == true) { Inputs.var_part6.Value = Inputs.var_gasketpartnum.Value; }
                if (Inputs.chk_glazingextrusion.Value == false) { Inputs.var_part6.Value = ""; }

                if ((Inputs.var_gasketpartnum.Value != "") && (Inputs.var_gasketqty.Value > 0)) { gasketcalc = Inputs.var_gasketqty.Value; }
                if ((Inputs.var_gasketpartnum.Value != "") && (Inputs.cmb_beamlength.Value == "Custom"))
                {
                    if (Inputs.dec_custombeamlength.Value < 1341) { gasketcalc = 1; }
                    if ((Inputs.dec_custombeamlength.Value > 1340) && (Inputs.dec_custombeamlength.Value < 2681)) { gasketcalc = 2; }
                    if ((Inputs.dec_custombeamlength.Value > 2680) && (Inputs.dec_custombeamlength.Value < 4021)) { gasketcalc = 3; }
                    if ((Inputs.dec_custombeamlength.Value > 4020) && (Inputs.dec_custombeamlength.Value < 5361)) { gasketcalc = 4; }
                }
                if (Inputs.var_part6.Value != "") { Inputs.var_partqty6.Value = gasketcalc; }
                if (Inputs.var_part6.Value == "") { Inputs.var_partqty6.Value = 0; }


                /*FG description and number String generation*/
                Inputs.var_fgdesc.Value = "";
                Inputs.var_fgvalidity.Value = "";

                Inputs.var_fgdesc.Value = PCLookUp.DataLookup("TENUK_FG_MasterTable.", "fg_desc", Inputs.var_fgnum.Value);

                if (Inputs.var_fgnum.Value == "") { Inputs.var_fgvalidity.Value = "** ERROR ** No Finished Goods number found."; }
                if ((Inputs.var_fgdesc.Value == "") && (Inputs.var_fgnum.Value != "")) { Inputs.var_fgvalidity.Value = "** ERROR ** The Finished Goods number has no TENUK_FG_MasterTable record."; }
                if ((Inputs.var_fgdesc.Value != "") && (Inputs.var_fgnum.Value != "")) { Inputs.var_fgvalidity.Value = "OK"; }

                /* Set standard Warranty Code */
								string warranty = "";
                warranty = PCLookUp.DataLookup("TENUK_FG_MasterTable", "std_warranty", Inputs.var_fgnum.Value);
                if (warranty == "") { Inputs.chr_warranty.Value = "1YR"; }
                if (warranty != "") { Inputs.chr_warranty.Value = warranty; }

                /*BOM Part Verification */

                Inputs.var_bomvalidity.Value = "OK";
                Inputs.var_ver1.Value = false;
                Inputs.var_ver2.Value = false;
                Inputs.var_ver3.Value = false;
                Inputs.var_ver4.Value = false;
                Inputs.var_ver5.Value = false;
                Inputs.var_ver6.Value = false;


							 /* Inputs.var_part1.Value */
				       if (Inputs.chk_partverification.Value == true)
				       {
				           available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part1.Value);
				       }
				       if (available_part) {
				           Inputs.var_ver1.Value = true;
				       }
				       if (Inputs.chk_partverification.Value == false) { Inputs.var_ver1.Value = false; }
				       if ((Inputs.chk_partverification.Value == true) && (Inputs.var_part1.Value != "") && (Inputs.var_ver1.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

								/* Inputs.var_part2.Value */
								if (Inputs.chk_partverification.Value == true)
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part2.Value);
								}
								if (available_part) {
									Inputs.var_ver2.Value = true;
								}
								if (Inputs.chk_partverification.Value == false) { Inputs.var_ver2.Value = false; }
								if ((Inputs.chk_partverification.Value == true) && (Inputs.var_part2.Value != "") && (Inputs.var_ver2.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

								/* Inputs.var_part3.Value */
								if (Inputs.chk_partverification.Value == true)
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part3.Value);
								}
								if (available_part) {
									Inputs.var_ver3.Value = true;
								}
								if (Inputs.chk_partverification.Value == false) { Inputs.var_ver3.Value = false; }
								if ((Inputs.chk_partverification.Value == true) && (Inputs.var_part3.Value != "") && (Inputs.var_ver3.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

								/* Inputs.var_part4.Value */
								if (Inputs.chk_partverification.Value == true)
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part4.Value);
								}
								if (available_part) {
									Inputs.var_ver4.Value = true;
								}
								if (Inputs.chk_partverification.Value == false) { Inputs.var_ver4.Value = false; }
								if ((Inputs.chk_partverification.Value == true) && (Inputs.var_part4.Value != "") && (Inputs.var_ver4.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

								/* Inputs.var_part5.Value */
								if (Inputs.chk_partverification.Value == true)
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part5.Value);
								}
								if (available_part) {
									Inputs.var_ver5.Value = true;
								}
								if (Inputs.chk_partverification.Value == false) { Inputs.var_ver5.Value = false; }
								if ((Inputs.chk_partverification.Value == true) && (Inputs.var_part5.Value != "") && (Inputs.var_ver5.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

								/* Inputs.var_part6.Value */
								if (Inputs.chk_partverification.Value == true)
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part6.Value);
								}
								if (available_part) {
									Inputs.var_ver6.Value = true;
								}
								if (Inputs.chk_partverification.Value == false) { Inputs.var_ver6.Value = false; }
								if ((Inputs.chk_partverification.Value == true) && (Inputs.var_part6.Value != "") && (Inputs.var_ver6.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }


                if ((Inputs.var_bomvalidity.Value == "** ERROR ** A component in the BOM has no Epicor Part Master Record.") && (Inputs.chk_erroron.Value == true))
                {
 										MessageBox.Show("A Component in the BOM does not exist in the Parts Master table, or the record has been made inactive. Please contact the Operations department to resolve this issue.",
										"Error",
										MessageBoxButtons.OK,
										MessageBoxIcon.Exclamation);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }

                /* Smart Code Generation */
                string coretype = "";
                string prodgroup = "";
                string beamlength = "";


                if (Inputs.cmb_coresolution.Value == "Core") { coretype = "COR"; }
                if (Inputs.cmb_coresolution.Value == "Solution") { coretype = "SOL"; }

                if (Inputs.cmb_productgroup.Value == "InQueue Merchandising") { prodgroup = "IQM"; }
                if (Inputs.cmb_productgroup.Value == "Tensaguide") { prodgroup = "TGU"; }

                if (Inputs.cmb_beamlength.Value != "Custom") { beamlength = Inputs.cmb_beamlength.Value; }
                if (Inputs.cmb_beamlength.Value == "Custom") { beamlength = Inputs.dec_custombeamlength.Value.ToString(); }

                /* Finds sector Smart Code */
                Inputs.var_sectorcode.Value = PCLookUp.DataLookup("", "sector_code", Inputs.cmb_sector.Value);

                string sumsalescat = "";

                if (Inputs.cmb_sumsalescat.Value == "Core") { sumsalescat = "COR"; }
                if (Inputs.cmb_sumsalescat.Value == "Solution") { sumsalescat = "SOL"; }

                Inputs.var_smartstring.Value = sumsalescat + "|" + coretype + "-" + prodgroup + "|" + Inputs.var_fgnum.Value + "|" + "XXX" + "|" + "XXX" + "|" + Inputs.var_beampartsuffix.Value + "|" + "XXX" + "|" + beamlength + "|" + "XXX" + "|" + "XXX" + "|" + "XXX" + "|" + "XXX" + "|" + Inputs.var_sectorcode.Value;

                Inputs.var_smart1.Value = coretype + "-" + prodgroup;
                Inputs.var_smart2.Value = Inputs.var_fgnum.Value + "+" + "XXX" + "-" + "XXX" + "-" + Inputs.var_beampartsuffix.Value + "-" + "XXX" + "-" + beamlength + "-" + "XXX" + "-" + "XXX" + "-" + "XXX" + "-" + "XXX";
                Inputs.var_smart3.Value = Inputs.var_sectorcode.Value;

                /* Comment Strings */


                /* Standard lengths */
                if (Inputs.cmb_beamlength.Value != "Custom")
                {
                    Inputs.var_linecomment.Value = Inputs.cmb_beamlength.Value + "mm " + Inputs.cmb_beamtype.Value + " in " + Inputs.cmb_beamfinish.Value + " finish.";
                    Inputs.var_jobcomment.Value = Inputs.cmb_beamlength.Value + "mm " + Inputs.cmb_beamtype.Value + " in " + Inputs.cmb_beamfinish.Value + " finish.";
                }

                /* Custom length */
                if (Inputs.cmb_beamlength.Value == "Custom")
                {
                    Inputs.var_linecomment.Value = Inputs.dec_custombeamlength.Value.ToString() + "mm  " + Inputs.cmb_beamtype.Value + " in " + Inputs.cmb_beamfinish.Value + " finish.";
                    Inputs.var_jobcomment.Value =  Inputs.dec_custombeamlength.Value.ToString()  + "mm " + Inputs.cmb_beamtype.Value + " in " + Inputs.cmb_beamfinish.Value + " finish.";
                }

                /* Label comment line */
                Inputs.var_labeldesc.Value = Inputs.var_linecomment.Value;



                /* Validity Routine */

                /* Checks if FG Number passed to sales line is less than 50 Characters */
                decimal smartcount = 0m;
                smartcount = 0;
                smartcount = Inputs.var_smart2.Value.Length;
                if (smartcount > 50)
                {
										MessageBox.Show("The Finished Goods Number exceeds the Maximum number of Characters allowed in the Sales Line Part Number Field......",
										"Note",
										MessageBoxButtons.OK,
										MessageBoxIcon.Information);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }

                /* Checks if fg description has been passed */
                string fgdesccheck = "";
                string fgdesc = "";
                fgdesccheck = Inputs.var_fgdesc.Value;
                fgdesc = "";
                if (fgdesccheck == "")
                {
										string Description = "";
                    available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.var_fgnum.Value, out Description);
                    if (available_part) {
                        fgdesc = Description;
                    }

                    if (fgdesc != "")
                    {
                        Inputs.var_fgdesc.Value = fgdesc;
                        Inputs.var_fgvalidity.Value = "OK";
                    }
                    if (fgdesc == "")
                    {
												MessageBox.Show("No Description has been returned for the Finished Goods Number.........",
												"Note",
												MessageBoxButtons.OK,
												MessageBoxIcon.Information);

										    PageLeaveFunctions.SetNextInputPage(1);
												Args.Cancel = true;

                    }
                }

                /* Calculates Net Weight */

                decimal CompWeight1 = 0m;
                decimal CompWeight2 = 0m;
                decimal CompWeight3 = 0m;
                decimal CompWeight4 = 0m;
                decimal CompWeight5 = 0m;
                decimal CompWeight6 = 0m;

								decimal partWeight = 0.0m;

								/* CompWeight1 */
								if (Inputs.var_part1.Value != "")
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part1.Value);

								}
								if (available_part) {
									UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part1.Value, out partWeight);
									CompWeight1 = partWeight * Inputs.var_partqty1.Value;
								}

								/* CompWeight2 */
								if (Inputs.var_part2.Value != "")
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part2.Value);

								}
								if (available_part) {
									UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part2.Value, out partWeight);
									CompWeight2 = partWeight * Inputs.var_partqty2.Value;
								}

								/* CompWeight3 */
								if (Inputs.var_part3.Value != "")
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part3.Value);

								}
								if (available_part) {
									UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part3.Value, out partWeight);
									CompWeight3 = partWeight * Inputs.var_partqty3.Value;
								}

								/* CompWeight4 */
								if (Inputs.var_part4.Value != "")
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part4.Value);

								}
								if (available_part) {
									UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part4.Value, out partWeight);
									CompWeight4 = partWeight * Inputs.var_partqty4.Value;
								}

								/* CompWeight5 */
								if (Inputs.var_part5.Value != "")
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part5.Value);

								}
								if (available_part) {
									UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part5.Value, out partWeight);
									CompWeight5 = partWeight * Inputs.var_partqty5.Value;
								}

								/* CompWeight6 */
								if (Inputs.var_part6.Value != "")
								{
									available_part = UDMethods.PartExists(Context.CompanyID, Inputs.var_part6.Value);

								}
								if (available_part) {
									UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part6.Value, out partWeight);
									CompWeight6 = partWeight * Inputs.var_partqty6.Value;
								}

                /* Totals the individual component weights */
                Inputs.var_netweight.Value = CompWeight1 + CompWeight2 + CompWeight3 + CompWeight4 + CompWeight5 + CompWeight6;

                /* Used to display Netweight list incase verification is required */
                Inputs.var_weightlookup.Value = "1(" + CompWeight1.ToString() + ")" + " - 2(" + CompWeight2.ToString() + ")" + " - 3(" + CompWeight3.ToString() + ")" + " - 4(" + CompWeight4.ToString() + ")" + " - 5(" + CompWeight5.ToString() + ")" + " - 6(" + CompWeight6.ToString() + ")";

                /* ************************************************************************************************************************ */

                /* Returns Commodity Code from CSV */

                string commoditystart = "";

                commoditystart = PCLookUp.DataLookup("TENUK_FG_MasterTable", "commodity_code", Inputs.var_fgnum.Value);

                /* If no commodity code is returned from the CSV file it checks the parts master table */
                string partclass = "";
                partclass = "";
                if (commoditystart == "")
                {

										string PartClass = "";
                    available_part = UDMethods.GetPartClass(Context.CompanyID, Inputs.var_fgnum.Value, out PartClass);
                    if (available_part) {
                        partclass = PartClass;
                    }

                }
                /* As Part Class is used to assign Commodity Code */
                if ((commoditystart == "") && (partclass != ""))
                {
									string CommodityCode = "";
                  bool available_partclass = UDMethods.GetPartCommodityCode(Context.CompanyID, partclass, out CommodityCode);
                  if (available_partclass) {
                      commoditystart = CommodityCode;
                  }
                }

                /* Converts commodity code based on Current Company */

                string commodityconverted = "";
                commodityconverted = "";

                if (commoditystart != "")
                {
                    commodityconverted = PCLookUp.DataLookup("CommodityCode_Conversion_Matrix", Context.CompanyID, commoditystart);
                }

                /* Verifies Commodity code exists in IComCode Table within Epicor */
                string commoditycheck = "";
                commoditycheck = "";
                if (commodityconverted != "")
                {
									  string ICommCodeImportCode = "";
                    bool available_ICommCode = UDMethods.GetICommImportCode(Context.CompanyID, commodityconverted, out ICommCodeImportCode);
                    if (available_ICommCode) {
                        commoditycheck = ICommCodeImportCode;
                    }

                }

                if (commodityconverted != commoditycheck)
                {

										MessageBox.Show("The Configurator has assigned a Commodity code that does not have a corresponding Epicor record, please contact an Epicor Administrator to correct this error.",
										"Error",
										MessageBoxButtons.OK,
										MessageBoxIcon.Exclamation);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }

                if (commodityconverted != "") { Inputs.var_commodity.Value = commodityconverted; }
                if (commodityconverted == "") { Inputs.var_commodity.Value = commoditystart; }


                if (Inputs.var_commodity.Value == "")
                {
										MessageBox.Show("The Configurator has been unable to determine the Commodity Code for the chosen configuration. Please contact the Operations department.",
										"Error",
										MessageBoxButtons.OK,
										MessageBoxIcon.Exclamation);

								    PageLeaveFunctions.SetNextInputPage(1);
										Args.Cancel = true;

                }
                else if ((Inputs.var_commodity.Value != "") || (Inputs.var_commodity.Value == commoditycheck))
                {
                    PageLeaveFunctions.SetNextInputPage(4);
                }

                /* ********************************************************************************************************************************************************************************** */

                /* Pack Comment */

								string Mode = "";
								int CustID = 0;
								int OrderNum = 0;
								int QuoteNum = 0;
								string CustCountry = "";
								string CustName = "";
								string OTSID = "";
								bool OTS = false;
								int OTSCountry = 0;
								string currencyCode = "";

								/* Determines Current Configuration Mode and order/quote specifics */
								Mode = Context.Entity;
								CustID = 0; /* Set to Zero when not used for testing*/
								CustCountry = "";
								OrderNum = Context.OrderNumber;
								QuoteNum = Context.QuoteNumber;


								/* ********************************************************************************************************************************************************************************** */
								if (Mode == "Quote")
								{
										/* Retrieve details from Quote Header */
										bool available_QuoteHed = UDMethods.GetQuoteHed(Context.CompanyID, QuoteNum, out CustID, out OTSID, out OTS, out currencyCode);

								    if (available_QuoteHed)
								    {
								        /*Inputs.var_custid.Value = CustID;*/
								        /*Inputs.var_custcurrency.Value = currencyCode;*/
								        /*Inputs.var_custcurrency1.Value = currencyCode;*/
								    }                  

										/* ************************************************************** */

										/* Retrieves customer name using customer ID */
										bool available_Customer = UDMethods.GetCustomer(Context.CompanyID, CustID, out CustName, out CustCountry, out currencyCode);

										/* If a saved shipto address is specified gets country info */
										if (OTSID != "")
										{
										    bool available_ShipTo = UDMethods.GetShipToCountryNum(Context.CompanyID, OTSID, out OTSCountry);
										    bool available_Country = UDMethods.GetCountryDescription(Context.CompanyID, OTSCountry, out CustCountry);
										}

									  Inputs.var_orderdtl.Value = "Mode: " + Mode + "  |  Order/Quote No: " + QuoteNum.ToString() + "   | Cust: " + CustName + "  |   ShipCountry: " + CustCountry;
									  Inputs.var_shipcountry.Value = CustCountry;	
								}

								/* ********************************************************************************************************************************************************************************** */
								if (Mode == "Order")
								{
									/* Retrieve details from Order Header */
							      bool available_OrderHed = UDMethods.GetOrderHed(Context.CompanyID, OrderNum, out CustID, out OTSID, out OTS, out currencyCode);

							      if (available_OrderHed)
							      {
							        /*Inputs.var_custid.Value = CustID;
							        Inputs.var_custcurrency.Value = currencyCode;
							        Inputs.var_custcurrency1.Value = currencyCode;*/
							      }

							      /* Retrieves customer name using customer ID */
							      bool available_Customer = UDMethods.GetCustomer(Context.CompanyID, CustID, out CustName, out CustCountry, out currencyCode);

							      /* If a saved shipto address is specified gets country info */
							      if (OTSID != "")
							      {
							          bool available_ShipTo = UDMethods.GetShipToCountryNum(Context.CompanyID, OTSID, out OTSCountry);
												bool available_Country = UDMethods.GetCountryDescription(Context.CompanyID, OTSCountry, out CustCountry);
							      }

									 Inputs.var_orderdtl.Value = "Mode: " + Mode + "  |  Order/Quote No: " + OrderNum.ToString() + "   | Cust: " + CustName + "  |   ShipCountry: " + CustCountry;
									 Inputs.var_shipcountry.Value = CustCountry;
								}


								/* ********************************************************************************************************************************************************************************** */
								if (Mode == "PcStatus")
								{
										Inputs.var_orderdtl.Value = "Mode: " + Mode;
										/* Inputs.var_shipcountry.Value = "Albania". */
								}

                /* ********************************************************************************************************************************************************************************** */

                string palletlookup = "";

                palletlookup = "";

                /* Finds Pallet lookup from csv based on Shipping country*/
                palletlookup = PCLookUp.DataLookup("CountryPalletList", "pallet_type", Inputs.var_shipcountry.Value);

                if (palletlookup != "")
                {
                    Inputs.var_palletlookup.Value = palletlookup + "_Lookup";
                    Inputs.var_packcomment.Value = PCLookUp.DataLookup("CountryPalletList", "default_message", Inputs.var_shipcountry.Value);
                }

                if (palletlookup == "") { Inputs.var_palletlookup.Value = "All_Lookup"; }

			}

       /* Determine the tgbe operation cycle time based on FG Number */


       /* Find tgbe cycle_time_selector */
       string tgbecycletimelookup = "";
       tgbecycletimelookup = "std";


       /* Returns cycletime from Master FG List CSV */

       string tgbecycletimeselector = "";

       tgbecycletimeselector = tgbecycletimelookup + "_op_time_min";

       Inputs.var_op10time.Value = UDMethods.ConvertToDecimal(PCLookUp.DataLookup("", tgbecycletimeselector, Inputs.var_fgnum.Value));




















