string baserange_search = "";

baserange_search = "";

baserange_search = PCLookUp.DataLookup("BaseRange", "base_finish", Inputs.cmb_baseselection.Value);

Inputs.var_basefinishlookup.Value = "base_" + baserange_search;
Inputs.var_coverfinishlookup.Value = "cover_" + baserange_search;

Inputs.var_basecoverpartnum.Value = PCLookUp.DataLookup("BaseRange", "cover_part_no", Inputs.cmb_baseselection.Value);

Inputs.var_smartcodebase.Value = PCLookUp.DataLookup("BaseRange", "smartcode_entry", Inputs.cmb_baseselection.Value);


Inputs.var_baseprotectoroptionon.Value = PCLookUp.DataLookup("BaseRange", "base_protector", Inputs.cmb_baseselection.Value);

Inputs.var_basekitpartnum.Value = PCLookUp.DataLookup("BaseRange", "base_kit_part_no", Inputs.cmb_baseselection.Value);

if ((Inputs.var_basekitpartnum.Value!="") && ( Inputs.chk_utilitypair.Value == false)) {Inputs.var_basekitqty.Value = 1;}
if ((Inputs.var_basekitpartnum.Value!="") && ( Inputs.chk_utilitypair.Value == true)) {Inputs.var_basekitqty.Value = 2;}
if (Inputs.var_basekitpartnum.Value=="") {Inputs.var_basekitqty.Value= 0;}

Inputs.var_baseboxpackinterim.Value = PCLookUp.DataLookup("BaseRange", "base_box_part_no", Inputs.cmb_baseselection.Value);


if (Inputs.var_postutilityringlookup.Value == "True") {Inputs.var_utilityringpartnum.Value = "00065-33S";}
if (Inputs.var_postutilityringlookup.Value == "False") {Inputs.var_utilityringpartnum.Value = "";}
if (Inputs.var_postutilityringlookup.Value == "") {Inputs.var_utilityringpartnum.Value = "";}

if (Inputs.var_utilityringpartnum.Value=="") { Inputs.var_utilityringqty.Value = 0;}
if (Inputs.var_utilityringpartnum.Value!="") { Inputs.var_utilityringqty.Value = 2;}
if ((Inputs.var_utilityringpartnum.Value!="") && ( Inputs.chk_utilitypair.Value == true)) { Inputs.var_utilityringqty.Value = 4;}

if ((Inputs.var_basefinishlookup.Value == "base_universal") && ( Inputs.var_postfinishcolumnlookup.Value == "popular_post")) {Inputs.var_popsboxpartnum.Value = "02600-000";}
if ((Inputs.var_basefinishlookup.Value == "base_universal") && ( Inputs.var_postfinishcolumnlookup.Value != "popular_post")) {Inputs.var_popsboxpartnum.Value = "";}
if ((Inputs.var_basefinishlookup.Value != "base_universal") && ( Inputs.var_postfinishcolumnlookup.Value != "popular_post")) {Inputs.var_popsboxpartnum.Value = "";}
if ((Inputs.var_basefinishlookup.Value != "base_universal") && ( Inputs.var_postfinishcolumnlookup.Value == "popular_post")) {Inputs.var_popsboxpartnum.Value = "";}
if (Inputs.var_popsboxpartnum.Value == "") {Inputs.var_basepackingpartnum.Value = Inputs.var_baseboxpackinterim.Value;}

if (Inputs.var_basepackingpartnum.Value != "") {Inputs.var_basepackingqty.Value = 1;}
if (Inputs.var_basepackingpartnum.Value == "") {Inputs.var_basepackingqty.Value = 0;}

if ((Inputs.var_popsboxpartnum.Value != "") && ( Inputs.chk_utilitypair.Value == true)) {Inputs.var_popboxqty.Value = 2;}
if ((Inputs.var_popsboxpartnum.Value != "") && ( Inputs.chk_utilitypair.Value == false)) {Inputs.var_popboxqty.Value = 2;}
if (Inputs.var_popsboxpartnum.Value == "") {Inputs.var_popboxqty.Value = 0;}

/* Pops Box Spacer */
if ((Inputs.var_popsboxpartnum.Value == "02600-000") && ( Inputs.var_postfinishcolumnlookup.Value == "popular_post")) {Inputs.var_centrepacking.Value = "01306-000";}
if (Inputs.var_centrepacking.Value == "01306-000") {Inputs.var_centrepackqty.Value = 1;}

/* HeavyDuty Spacer */
if (Inputs.var_basefinishlookup.Value == "base_heavyduty") {Inputs.var_centrepacking.Value = "08904-000";}
if (Inputs.var_centrepacking.Value == "01306-000") {Inputs.var_centrepackqty.Value = 1;}

/* A temporary suspension can be added to a selection */
string isactive1 = "";
isactive1 = "";
isactive1 = PCLookUp.DataLookup("BaseRange", "suspend_selection", Inputs.cmb_baseselection.Value);
if (isactive1 == "True") {


	MessageBox.Show("A temporary suspension has been requested for that selection, please contact the Operations department or make a different selection..",
	"Note",
	MessageBoxButtons.OK,
	MessageBoxIcon.Information);


}


