  Inputs.chk_alarmfobrqd.Invisible = false;
  Inputs.dec_alarmfixedqty.Invisible = false;
  Inputs.chk_alarmfixedqtyrqd.Invisible = false;
}

if (Inputs.var_cassfgdecider.Value != "Alarm") {
  
  Inputs.chk_alarmfobrqd.Invisible = true;
  Inputs.dec_alarmfixedqty.Invisible = true;
  Inputs.chk_alarmfixedqtyrqd.Invisible = true;
