  Inputs.var_botwebpart.Value = Inputs.cmb_dualwebbingfinish.Value;
}

if (Inputs.cmb_dualwebbingfinish.Value == "") {
  Inputs.var_botwebpart.Value = "";
}

/* Set the BOM Webbing Qty */
string dualwebuom = "";
dualwebuom = "";

string PartIUM = "";
bool available_part = UDMethods.GetPartIUM(Context.CompanyID,Inputs.var_botwebpart.Value, out PartIUM);
if (available_part) {
  dualwebuom = PartIUM;
}


if (dualwebuom == "EA") {
  Inputs.var_botwebqty.Value = 1 * Inputs.var_qtymultiplier.Value;
}

if ((dualwebuom == "M") && ( Inputs.dec_customweblength.Value == 0) && ( Inputs.cmb_webbinglength.Value != "")) {
  Inputs.var_botwebqty.Value = (UDMethods.ConvertToDecimal(Inputs.cmb_webbinglength.Value) + 0.15m) * Inputs.var_qtymultiplier.Value;
}

if ((dualwebuom == "M") && ( Inputs.dec_customweblength.Value == 0) && ( Inputs.cmb_dualweblength.Value != "")) {
  Inputs.var_botwebqty.Value = (UDMethods.ConvertToDecimal(Inputs.cmb_dualweblength.Value) + 0.15m) * Inputs.var_qtymultiplier.Value;
}

if ((dualwebuom == "M") && ( Inputs.dec_customweblength.Value != 0)) {
  Inputs.var_botwebqty.Value = (Inputs.dec_customweblength.Value + 0.15m) * Inputs.var_qtymultiplier.Value;
}

if (Inputs.var_botwebpart.Value == "") {
  Inputs.var_botwebqty.Value = 0;
}



