/* Sets the parameters for Cut Length Webbing */

/* Finds cut length suffix used in dynamic list query Inputs.cmb_dualwebbingfinish.Value  from csv */
string dualweblengthcut = "";
dualweblengthcut = "";
dualweblengthcut = PCLookUp.DataLookup("WebbingLengthConversionFactors", "cutlength_factor", Inputs.cmb_dualweblength.Value);

if (dualweblengthcut != "") {Inputs.var_dualweblengthcut.Value = dualweblengthcut;}
if (dualweblengthcut == "") {Inputs.var_dualweblengthcut.Value = "";}

/* Determines if cut length is active used in dynamic list query Inputs.cmb_dualwebbingfinish.Value  from csv */
string dualweblengthcutactive = "";
dualweblengthcutactive = "";

if ((Inputs.chk_dualcustomwebbing.Value == false) && ( Inputs.cmb_dualwebbingfinish.Value == "")) {
  
  dualweblengthcutactive = PCLookUp.DataLookup("WebbingLengthConversionFactors", "SWEB_active", Inputs.cmb_dualweblength.Value);
  
if (dualweblengthcutactive == "TRUE") {Inputs.var_dualweblengthcutactive.Value = "TRUE";}
if (dualweblengthcutactive != "TRUE") {Inputs.var_dualweblengthcutactive.Value = "FALSE";}
  
  /* Set the Web Format parameter */
  
if (Inputs.var_dualweblengthcutactive.Value == "TRUE") {Inputs.cmb_webformat.Value = "CUT";}
if (Inputs.var_dualweblengthcutactive.Value == "FALSE") {Inputs.cmb_webformat.Value = "ROLL";}
  
}

if ((Inputs.chk_dualcustomwebbing.Value == true) && ( Inputs.cmb_dualwebbingfinish.Value == "")) {
  
  dualweblengthcutactive = PCLookUp.DataLookup("WebbingLengthConversionFactors", "CWEB_active", Inputs.cmb_dualweblength.Value);
  
if (dualweblengthcutactive == "TRUE") {Inputs.var_dualweblengthcutactive.Value = "TRUE";}
if (dualweblengthcutactive != "TRUE") {Inputs.var_dualweblengthcutactive.Value = "FALSE";}
  
  /* Set the Web Format parameter */
  
if (Inputs.var_dualweblengthcutactive.Value == "TRUE" ) {Inputs.cmb_webformat.Value = "CUT";}
if (Inputs.var_dualweblengthcutactive.Value == "FALSE") {Inputs.cmb_webformat.Value = "ROLL";}
  
}

if (Inputs.var_dualweblengthcut.Value != "") {Inputs.var_weblengthcut.Value = Inputs.var_dualweblengthcut.Value;}












