if (Inputs.var_defaultpackaspairs.Value == "True") {Inputs.chk_utilitypair.Value = true;}

/* Looks up if post can have reflective strips from csv */
Inputs.var_reflectiveon.Value = PCLookUp.DataLookup("PostSelectionRev", "reflective_strip", Inputs.cmb_postselection.Value);
if (Inputs.var_reflectiveon.Value=="") {Inputs.rbn_reflectivestrip.Value = "3";}

/* Looks up if post can have an Allen Key from csv */
Inputs.var_allenkeyoption.Value = PCLookUp.DataLookup("PostSelectionRev", "allenkey_option", Inputs.cmb_postselection.Value);
if ( Inputs.var_allenkeyoption.Value != "TRUE") {Inputs.chk_allenkey.Value = false;}

