/* Updates the Line Qty if the value has subsequently been changed since the original configuration */

string mode = "";
mode = Context.Entity;

string currentcompany = "";
currentcompany = Context.CompanyID;

if (mode == "Order") {
  decimal orderlineqtychk = 0m;
  orderlineqtychk = 0;
  
  decimal currentorder = 0m;
  currentorder = Context.OrderNumber;
  
  decimal currentline = 0m;
  currentline = Context.OrderLineNumber;

  decimal SellingQuantity = 0m;
  
  bool available_OrderDtl = UDMethods.GetOrderDtlSellingQuantity(Context.CompanyID, (int)currentorder, (int)currentline, out SellingQuantity);
  if (available_OrderDtl) {
    orderlineqtychk = SellingQuantity;

  }
  
  if (orderlineqtychk > 0) {Inputs.dec_soqty.Value = SellingQuantity;}
}


if (mode == "Quote") {
  decimal quotelineqtychk = 0m;
  quotelineqtychk = 0;
  
  decimal currentquote = 0m;
  currentquote = Context.QuoteNumber;
  
  decimal currentquoteline = 0m;
  currentquoteline = Context.QuoteLineNumber;

  decimal SellingExpectedQty = 0m;
  
  bool available_QuoteDtl = UDMethods.GetQuoteDtlSellingExpectedQty(Context.CompanyID, (int)currentquote, (int)currentquoteline, out SellingExpectedQty);
  if (available_QuoteDtl) {
    quotelineqtychk = SellingExpectedQty;
  }
  
if (quotelineqtychk > 0) {Inputs.dec_soqty.Value = SellingExpectedQty;}
}














