                if (Inputs.chk_duallineoption.Value == true) { postduallinecommenttype = " dualline"; }

                /* Removes the finish suffix reference from the post finish */
                string replacerefpostfinish = "";
                /*replacerefpostfinish = REPLACE(Inputs.cmb_postfinishselection.Value, "(", "+");
                postfinishcommenttype = TRIM(replacerefpostfinish.Entry(0, "+"));*/

								replacerefpostfinish = Inputs.cmb_postfinishselection.Value.ToString().Replace("(", "+");
								postfinishcommenttype = replacerefpostfinish.Entry(0, "+").Trim();

                /* Removes the finish suffix reference from the base cover finish */
                string replacerefbasecoverfinish = "";
