                if ((Inputs.cmb_baseselection.Value != "As Supplied") && (Inputs.cmb_basecoverfinish.Value == "")) { basecoverfinishcommenttype = ", "; }
                if ((Inputs.cmb_baseselection.Value != "As Supplied") && (Inputs.cmb_basecoverfinish.Value != "")) { basecoverfinishcommenttype = " with " + amendedrefbasecoverfinish + " base cover, "; }

                /* Set the top cassette comment variable */
