										string Description = "";
                    available_part = UDMethods.GetPartDescription(Context.CompanyID, Inputs.var_webpartnum.Value,out Description);

                    if (available_part) {
                        webcommentdesc = Description;
                    }
                    /* Removes the Cut and Stitch reference from the webbing part description */
