                Inputs.var_jobcomments.Value = flatpack + postcommenttype + postduallinecommenttype.ToLower() + " post in " + postfinishcommenttype.ToLower() + ", " + basecommenttype.ToLower() + " base" + basecoverfinishcommenttype.ToLower() + cass1commenttype.ToLower() + webbingcommenttype.ToLower() + tapeendcommenttype.ToLower();




