                decimal CompWeight11a = 0m;
                decimal CompWeight12a = 0m;
                decimal CompWeight13a = 0m;
                decimal CompWeight14a = 0m;
                decimal CompWeight15a = 0m;
                decimal CompWeight16a = 0m;
                decimal CompWeight17a = 0m;
                decimal CompWeight18a = 0m;
                decimal CompWeight19a = 0m;
                decimal CompWeight20a = 0m;
                decimal CompWeight21a = 0m;
                decimal CompWeight22a = 0m;
                decimal CompWeight23a = 0m;

								decimal PartWeight = 0m;

								/* CompWeight1a  */
                if (Inputs.var_postpartnum.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_postpartnum.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight1a = PartWeight * Inputs.var_postqty.Value;
                }

								/* CompWeight2a  */
                if (Inputs.var_postpackingpartnum.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_postpackingpartnum.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight2a = PartWeight * Inputs.var_postpackingqty.Value;
                }

								/* CompWeight3a  */
                if (Inputs.var_allenkeypartnum.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_allenkeypartnum.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight3a = PartWeight * Inputs.var_allenkeyqty.Value;
                }
								/* CompWeight4a  */
                if (Inputs.var_reflectivepartnum.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_reflectivepartnum.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight4a = PartWeight * Inputs.var_reflectiveqty.Value;
                }

								/* CompWeight5a  */
                if (Inputs.var_slugpartnum.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_slugpartnum.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight5a = PartWeight * Inputs.var_slugqty.Value;
                }

								/* CompWeight6a  */
                if (Inputs.var_centrepacking.Value != "")
                {
                    available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_centrepacking.Value, out PartWeight);
                }
                if (available_part) {
                    CompWeight6a = PartWeight * Inputs.var_centrepackqty.Value;
                }

								/* CompWeight7a  */
                if (Inputs.var_utilityringpartnum.Value != "")
                {
