// Enter valid C# code and do not forget this method returns a boolean value.
Erp.Tables.Currency currency;

CurrencyCode = "";

currency = (from Currency_Row in Db.Currency
		    where (Currency_Row.Company == Company && Currency_Row.BaseCurr == true)
		    select Currency_Row).FirstOrDefault();

if (currency == null) { return false; }
else {
  CurrencyCode =  currency.CurrencyCode;
  return true;
}

