}


/* Determines the Part type to correctly assign the part placeholder */

if (Inputs.cmb_chassisselect.Value != "") {
  string partype = "";
  bool isnonstock = false;
  partype = "";
  isnonstock = false;

	string SourceType = "";
  bool NonStock = false;

  bool available_partplant = UDMethods.GetPartPlant(Context.CompanyID, Inputs.cmb_chassisselect.Value, out SourceType, out NonStock);
  if (available_partplant) {
    partype = SourceType;
    isnonstock = NonStock;
  }



  /*if (available_partplant) {
    partype = partplant.sourcetype;
    isnonstock = partplant.nonstock;
  }*/

  /* If a Purchased Part and Stocked */
  if (partype == "P") {
    Inputs.var_part13.Value = Inputs.cmb_chassisselect.Value;
    Inputs.var_partqty13.Value=1;
    Inputs.var_part1.Value = "";
    Inputs.var_partqty1.Value=0;
  }

  /* If a Manufactured Part and not stocked */
  if ((partype == "M") && ( isnonstock == true)) {
    Inputs.var_part1.Value = Inputs.cmb_chassisselect.Value;
    Inputs.var_partqty1.Value=1;
    Inputs.var_part13.Value = "";
    Inputs.var_partqty13.Value=0;
  }

  /* If a Manufactured Part and stocked */
  if ((partype == "M") && ( isnonstock == false)) {
    Inputs.var_part13.Value = Inputs.cmb_chassisselect.Value;
    Inputs.var_partqty13.Value=1;
    Inputs.var_part1.Value = "";
    Inputs.var_partqty1.Value=0;
  }

  /* If a Sales Kit Part and not stocked */
  if ((partype == "K") && ( isnonstock == true)) {
    Inputs.var_part1.Value = Inputs.cmb_chassisselect.Value;
    Inputs.var_partqty1.Value=1;
    Inputs.var_part13.Value = "";
    Inputs.var_partqty13.Value=0;
  }

  /* If a Sales Kit Part and stocked */
  if ((partype == "K") && ( isnonstock == false)) {
    Inputs.var_part13.Value = Inputs.cmb_chassisselect.Value;
    Inputs.var_partqty13.Value=1;
    Inputs.var_part1.Value = "";
    Inputs.var_partqty1.Value=0;
  }


}

