				       if ((Inputs.chk_partverification.Value == true) && (Inputs.var_part10.Value != "") && (Inputs.var_ver10.Value == false)) { Inputs.var_bomvalidity.Value = "** ERROR ** A component in the BOM has no Epicor Part Master Record."; }

