/* Switch to Administrator View Mode */

if (Inputs.chk_pricelistadminmode.Invisible == true) {
    Inputs.var_seg2active.Invisible = false;
    Inputs.var_seg3active.Invisible = false;
    Inputs.var_seg4active.Invisible = false;
