        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_part13.Value, out PartWeight);
        }
        if (available_part) {
            CompWeight13 = PartWeight * Inputs.var_partqty13.Value;
        }

        /* Totals the individual component weights if make direct */
        if (Inputs.chk_rework.Value == true)
        {

            /* Totals the individual component weights */
            if (Inputs.rad_supplytype.Value == "2")
            {
                Inputs.var_netweight.Value = CompWeight1 + CompWeight2 + CompWeight3 + CompWeight4 + CompWeight5 + CompWeight6 + CompWeight7 + CompWeight8 + CompWeight9 + CompWeight10 + CompWeight11 + CompWeight12 + CompWeight13;

                /* Used to display Netweight list incase verification is required */
                Inputs.var_weightlookup.Value = "1(" + CompWeight1.ToString() + ")" + " - 2(" + CompWeight2.ToString() + ")" + " - 3(" + CompWeight3.ToString() + ")" + " - 4(" + CompWeight4.ToString() + ")" + " - 5(" + CompWeight5.ToString() + ")" + " - 6(" + CompWeight6.ToString() + ")" + " - 7(" + CompWeight7.ToString() + ")" + " - 8(" + CompWeight8.ToString() + ")" + " - 9(" + CompWeight9.ToString() + ")" + " - 10(" + CompWeight10.ToString() + ")" + " - 11(" + CompWeight11.ToString() + ")" + " - 12(" + CompWeight12.ToString() + ")" + " - 13(" + CompWeight13.ToString() + ")";
            }
        }

        if ((Inputs.chk_rework.Value == false) && (Inputs.rad_supplytype.Value == "2"))
        {
            available_part = UDMethods.GetPartWeight(Context.CompanyID, Inputs.var_sku.Value, out PartWeight);

            if (available_part) {
                Inputs.var_netweight.Value = PartWeight;
            }
        }

        /* Set the default Operation */
        if (Inputs.chk_rework.Value == false)
        {
            Inputs.var_optouse.Value = Inputs.var_op1.Value;
        }

        if ((Inputs.chk_rework.Value == true) && (Inputs.var_fgstockpart.Value == ""))
        {
            Inputs.var_optouse.Value = Inputs.var_op1.Value;
            /* Sets the Operation Std Time */
            Inputs.var_op10time.Value =  UDMethods.ConvertToDecimal(PCLookUp.DataLookup("TENUK_FG_MasterTable", "std_op_time_min", Inputs.var_fgprefix.Value));
        }

        if ((Inputs.var_fgstockpart.Value != "") && (Inputs.rad_supplytype.Value == "2"))
        {
            Inputs.var_optouse.Value = PCLookUp.DataLookup("RAPPostSelection_mfg", "fromstock_oper_code", Inputs.cmb_postselection.Value);
            Inputs.var_op10time.Value = 0;
        }

























