  partsuffix = PCLookUp.DataLookup("RAPPostFinish", "colorcode", Inputs.cmb_postfinish.Value);
if (partsuffix != "") {Inputs.var_part1.Value = Inputs.var_postprefix.Value + "-" + partsuffix;}
if (partsuffix == "") {Inputs.var_part1.Value = "";}
}

/* Calculate based on all bool available_part finishes */
if (Inputs.chk_postfinish.Value == true) {
if (Inputs.cmb_postfinish.Value !="") {Inputs.var_part1.Value = Inputs.cmb_postfinish.Value;}
if (Inputs.cmb_postfinish.Value =="") {Inputs.var_part1.Value = "";}
}

if (Inputs.var_part1.Value !="") {Inputs.var_partqty1.Value = 1;}
if (Inputs.var_part1.Value =="") {Inputs.var_partqty1.Value = 0;}

/* Set smartcode post finish */
if (partsuffix !="") {Inputs.var_smartcodepostfinish.Value = partsuffix;}
if (partsuffix =="") {Inputs.var_smartcodepostfinish.Value = "";}

/* *************************************************************************** */

