/* Calculate list price in customers currency */

string csvcurrlookup = "";
string csvcurradjtype = "";
csvcurrlookup = Inputs.var_basecurrency.Value + "-" + Inputs.var_custcurrency.Value;
csvcurradjtype = "";

//Inputs.var_exchangerate.Value = PCLookUp.DataLookup("SalesCurrencyRates", "rate_conversion", csvcurrlookup);
csvcurradjtype = PCLookUp.DataLookup("SalesCurrencyRates", "adjustment_type", csvcurrlookup);

if (csvcurradjtype == "direct") {
  Inputs.var_listpricecustcurrency.Value = Inputs.var_upchargecalctotal.Value * Inputs.var_exchangerate.Value;
}

if (csvcurradjtype == "inverse") {
  Inputs.var_listpricecustcurrency.Value = Inputs.var_upchargecalctotal.Value / Inputs.var_exchangerate.Value;
}








